    AND tipo = #{ tipo , jdbcType = INTEGER }

mapper.insert:
INSERT INTO calculoNpt (

    idCalculo


    , claveInstitucional


    , descripcion


    , tipo


    , formula


    , activo


    , insertFecha


    , insertidUsuario


    , updateFecha


    updateIdUsuario

) VALUES (

    #{ idCalculo , jdbcType = VARCHAR }


    , #{ claveInstitucional , jdbcType = VARCHAR }


    , #{ descripcion , jdbcType = VARCHAR }


    , #{ tipo , jdbcType = VARCHAR }


    , #{ formula , jdbcType = VARCHAR }


    , #{ activo , jdbcType = INTEGER }


    , #{ insertFecha , jdbcType = TIMESTAMP }


    , #{ insertidUsuario , jdbcType = VARCHAR }


    , #{ updateFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }

)

mapper.update:
UPDATE calculoNpt SET
updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
, updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR }

    , claveInstitucional = #{ claveInstitucional , jdbcType = VARCHAR }


    , descripcion = #{ descripcion , jdbcType = VARCHAR }


    , tipo = #{ tipo , jdbcType = VARCHAR }


    , formula = #{ formula , jdbcType = VARCHAR }


    , activo = #{ activo , jdbcType = INTEGER }

WHERE idCalculo = #{ idCalculo , jdbcType = VARCHAR }
