mapper.select:
SELECT idCita , idEstructura , idAgenda , idPaciente , idMedico , idTurno ,
titulo , fechaInicio , fechaFin , comentarios , estatus , insertFecha , insertIdUsuario ,
updateFecha , updateIdUsuario
FROM cita
WHERE 1 = 1

    AND idCita = #{ idCita , jdbcType = VARCHAR }


    AND idEstructura = #{ idEstructura , jdbcType = VARCHAR }


    AND idAgenda = #{ idAgenda , jdbcType = VARCHAR }


    AND idPaciente = #{ idPaciente , jdbcType = VARCHAR }


    AND idMedico = #{ idMedico , jdbcType = VARCHAR }


    AND idTurno  = #{ idTurno , jdbcType = INTEGER }

mapper.insert:
