mapper.update:
UPDATE cita

    updateFecha = #{updateFecha, jdbcType = TIMESTAMP }
    ,updateIdUsuario = #{updateIdUsuario, jdbcType = TIMESTAMP }
     ,idEstructura = #{idEstructura, jdbcType = VARCHAR }
    ,idAgenda = #{idAgenda, jdbcType = VARCHAR }
    ,idPaciente = #{idPaciente, jdbcType = VARCHAR }
    ,idMedico = #{idMedico, jdbcType = VARCHAR }
    ,idTurno = #{idTurno , jdbcType = INTEGER }
    ,titulo = #{titulo, jdbcType = VARCHAR}
    ,fechaInicio = #{fechaInicio, jdbcType = TIMESTAMP}
    ,fechaFin = #{fechaFin, jdbcType = TIMESTAMP}
    ,comentarios = #{comentarios, jdbcType = VARCHAR}
    ,estatus = #{estatus , jdbcType = INTEGER }                        

WHERE idCita = #{idCita, jdbcType = VARCHAR}

UPDATE cita

    estatus = 3

where idCita = #{idCita, jdbcType = VARCHAR}

UPDATE cita

    estatus = #{estatus, jdbcType = TIMESTAMP }            


