SELECT idDiagnosticoPaciente, idPrescripcion, fechaDiagnostico, idUsuarioDiagnostico, idDiagnostico, fechaFinDiagnostico, idUsuarioDiagnosticoTratado, idEstatusDiagnostico
FROM diagnosticoPaciente
WHERE 1=1

    AND idDiagnosticoPaciente = #{ idDiagnosticoPaciente , jdbcType = VARCHAR }


    AND idPrescripcion = #{ idPrescripcion , jdbcType = VARCHAR }


    AND idDiagnostico = #{ idDiagnostico , jdbcType = VARCHAR }


    AND fechaDiagnostico = #{ fechaDiagnostico , jdbcType = DATE }


    AND idUsuarioDiagnostico = #{ idUsuarioDiagnostico , jdbcType = VARCHAR }


    AND fechaFinDiagnostico = #{ fechaFinDiagnostico , jdbcType = DATE }


    AND idUsuarioDiagnosticoTratado = #{ idUsuarioDiagnosticoTratado , jdbcType = VARCHAR }


    AND idEstatusDiagnostico = #{ idEstatusDiagnostico , jdbcType = INTEGER }

LIMIT 1

