FROM fabricanteInsumo
WHERE 1=1

    AND idFabricanteInsumo = #{ idFabricanteInsumo , jdbcType = VARCHAR }


    AND idFabricante = #{ idFabricante , jdbcType = VARCHAR }


    AND idInsumo = #{ idInsumo , jdbcType = VARCHAR }


    AND idEstatus = #{ idEstatus , jdbcType = INTEGER }

LIMIT 0, 1

