mapper.select:
SELECT idFolio,prefijo,secuencia,longitud,idTipoDocumento,insertFecha,insertIdUsuario,updateFecha,updateIdUsuario
FROM folios
WHERE 1=1

    AND idFolio=#{idFolio}


    AND prefijo = #{prefijo}


    AND secuencia=#{secuencia}


    AND longitud=#{longitud}


    AND idTipoDocumento=#{idTipoDocumento}

mapper.update:
UPDATE folios 

    secuencia = #{secuencia},
    updateFecha = #{updateFecha},
    updateIdUsuario= #{updateIdUsuario}



WHERE idTipoDocumento=#{idTipoDocumento}
