mapper.select:
SELECT * FROM impresionEtiqueta
WHERE 1=1
 AND idImpresionEtiqueta = #{ idImpresionEtiqueta } 
 AND idSurtimiento = #{ idSurtimiento } 
 AND folio = #{ folio } 
 AND idMotivoReimpresion = #{ idMotivoReimpresion } 
LIMIT 1;

SELECT idImpresionEtiqueta, idSurtimiento, folio, numeroImpresiones
FROM impresionEtiqueta
WHERE 1=1
 AND idImpresionEtiqueta = #{ idImpresionEtiqueta } 
 AND idSurtimiento = #{ idSurtimiento } 
 AND folio = #{ folio } 
;

SELECT * FROM impresionEtiqueta
WHERE 1=1
 AND idImpresionEtiqueta = #{ idImpresionEtiqueta } 
 AND idSurtimiento = #{ idSurtimiento } 
 AND folio = #{ folio } 
 AND idMotivoReimpresion = #{ idMotivoReimpresion } 
;

mapper.insert:
INSERT INTO impresionEtiqueta (
    idImpresionEtiqueta
    , idSurtimiento
    , folio
     , contenido 
    , impresionExitosa
     , numeroImpresiones 
     , reimpresion 
     , idMotivoReimpresion 
     , notas 
    , insertFecha
    , insertIdUsuario
     , updateFecha 
     , updateIdUsuario 
) VALUES (
    #{ idImpresionEtiqueta }
    , #{ idSurtimiento }
    , #{ folio }
     , #{ contenido } 
    , #{ impresionExitosa }
     , #{ numeroImpresiones } 
     , #{ reimpresion } 
     , #{ idMotivoReimpresion } 
     , #{ notas } 
    , #{ insertFecha }
    , #{ insertIdUsuario }
     , #{ updateFecha } 
     , #{ updateIdUsuario } 
)

mapper.update:
UPDATE impresionEtiqueta SET
    updateIdUsuario = #{ updateIdUsuario }
    , updateFecha = #{ updateFecha }
    , impresionExitosa = #{ impresionExitosa }
     , idSurtimiento = #{ idSurtimiento } 
     , folio = #{ folio } 
     , contenido = #{ contenido } 
     , numeroImpresiones = #{ numeroImpresiones } 
     , reimpresion = #{ reimpresion } 
     , idMotivoReimpresion = #{ idMotivoReimpresion } 
     , reimpresion = #{ reimpresion } 
     , notas = #{ notas } 
WHERE idImpresionEtiqueta = #{ idImpresionEtiqueta } ;
