    #{ idLocalidadAVG , jdbcType = VARCHAR }


    , #{ localidad , jdbcType = VARCHAR }


    , #{ estatus , jdbcType = INTEGER }


    , #{ insertFecha , jdbcType = TIMESTAMP }


    , #{ updateFecha , jdbcType =  TIMESTAMP}


    , #{ updateIdUsuario , jdbcType = VARCHAR }

mapper.update:
