mapper.select:
SELECT idMinistracion , idSurtimientoInsumo , fechaProgramada , fechaMinistracion , idUsuarioMinistracion , cantidadMinistracion , idEstatusPrescripcion , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
FROM ministracion

    AND idMinistracion = #{ idMinistracion , jdbcType = VARCHAR }


    AND idSurtimientoInsumo = #{ idSurtimientoInsumo , jdbcType = VARCHAR }


    AND fechaProgramada = #{ fechaProgramada , jdbcType = VARCHAR }


    AND fechaMinistracion = #{ fechaMinistracion , jdbcType = VARCHAR }


    AND idUsuarioMinistracion = #{ idUsuarioMinistracion , jdbcType = VARCHAR }


    AND cantidadMinistracion = #{ cantidadMinistracion , jdbcType = VARCHAR }


    AND idEstatusPrescripcion = #{ idEstatusPrescripcion , jdbcType = VARCHAR }

mapper.insert:
INSERT INTO ministracion (

    idMinistracion


    , idSurtimientoInsumo


    , fechaProgramada


    , fechaMinistracion


    , idUsuarioMinistracion


    , cantidadMinistracion


    , idEstatusPrescripcion


    , insertIdUsuario 


    , insertFecha


    , updateIdUsuario


    , updateFecha

) VALUES (

    #{ idMinistracion , jdbcType = VARCHAR }


    , #{ idSurtimientoInsumo , jdbcType = VARCHAR }


    , #{ fechaProgramada , jdbcType = TIMESTAMP }


    , #{ fechaMinistracion , jdbcType = TIMESTAMP }


    , #{ idUsuarioMinistracion , jdbcType = VARCHAR }


    , #{ cantidadMinistracion , jdbcType = INTEGER }


    , #{ idEstatusPrescripcion , jdbcType = INTEGER }


    , #{ insertIdUsuario , jdbcType = VARCHAR }


    , #{ insertFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }


    , #{ updateFecha , jdbcType = TIMESTAMP }

)

mapper.update:
UPDATE ministracion SET
updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
, updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR }

    , fechaMinistracion = #{ fechaMinistracion , jdbcType = TIMESTAMP }


    , idUsuarioMinistracion = #{ idUsuarioMinistracion , jdbcType = VARCHAR }


    , cantidadMinistracion = #{ cantidadMinistracion , jdbcType = TIMESTAMP }


    , idEstatusPrescripcion = #{ idEstatusPrescripcion , jdbcType = INTEGER }

WHERE idMinistracion = #{ idMinistracion , jdbcType = VARCHAR }
