mapper.select:
SELECT idModulo , modulo , activo , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
FROM modulos
WHERE 1=1

    AND idModulo = #{ idModulo , jdbcType = VARCHAR }


    AND modulo = #{ modulo , jdbcType = VARCHAR }


    AND activo = #{ activo , jdbcType = INTEGER }

SELECT idModulo , modulo , activo , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
FROM modulos
WHERE 1=1

    AND idModulo = #{ idModulo , jdbcType = VARCHAR }


    AND modulo = #{ modulo , jdbcType = VARCHAR }


    AND activo = #{ activo , jdbcType = INTEGER }

ORDER BY orden asc

mapper.insert:
