mapper.update:
UPDATE modulos SET
updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
, updateUserId = #{ updateUserId , jdbcType = VARCHAR }

    , modulo = #{ modulo , jdbcType = VARCHAR }


    , activo = #{ activo , jdbcType = INTEGER }

