mapper.select:
SELECT idMotivoCancela , descripcion , activa , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
    FROM motivoCancelaPrescripcion
WHERE 1=1

    AND activa = #{ activa }


    AND descripcion = #{ descripcion }


    AND validaPrescripcion = #{ validaPrescripcion }


    AND validaMezcla = #{ validaMezcla }

LIMIT 1;

SELECT idMotivoCancela , descripcion , activa , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
    FROM motivoCancelaPrescripcion
