mapper.select:
SELECT idMotivoRechazo, motivo, activo, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario
    FROM motivosRechazo
WHERE activo = 1;

SELECT idMotivoRechazo, motivo, activo, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario
    FROM motivosRechazo
WHERE activo = 1

    AND idMotivoRechazo = #{ idMotivoRechazo }

LIMIT 1;
