mapper.select:
SELECT idNotificacion , idTransaccion , fecha , idTipoNotificacion , resumen , descripcion
FROM notificacion
WHERE 1=1

    AND idNotificacion = #{ idNotificacion , jdbcType = VARCHAR }


    AND idTransaccion = #{ idTransaccion , jdbcType = VARCHAR }


    AND idTipoNotificacion = #{ idTipoNotificacion , jdbcType = INTEGER }


    AND resumen = #{ resumen , jdbcType = VARCHAR }


    AND descripcion = #{ descripcion , jdbcType = VARCHAR }


    AND fecha = #{ fecha , jdbcType = INTEGER }

LIMIT 0, 1

SELECT idNotificacion , idTransaccion , fecha , idTipoNotificacion , resumen , descripcion
FROM notificacion
WHERE 1=1

    AND idNotificacion = #{ idNotificacion , jdbcType = VARCHAR }


    AND idTransaccion = #{ idTransaccion , jdbcType = VARCHAR }


    AND idTipoNotificacion = #{ idTipoNotificacion , jdbcType = INTEGER }


    AND resumen = #{ resumen , jdbcType = VARCHAR }


    AND descripcion = #{ descripcion , jdbcType = VARCHAR }


    AND fecha = #{ fecha , jdbcType = INTEGER }

mapper.insert:
INSERT INTO notificacion (

    idNotificacion


    , idTransaccion


    , descripcion


    , idTipoNotificacion


    , fecha


    , resumen


) VALUES (

    #{ idNotificacion , jdbcType = VARCHAR }


    , #{ idTransaccion , jdbcType = VARCHAR }


    , #{ descripcion , jdbcType = VARCHAR }


    , #{ idTipoNotificacion , jdbcType = INTEGER }


    , #{ fecha , jdbcType = TIMESTAMP }


    , #{ resumen , jdbcType = VARCHAR }


)

mapper.update:
UPDATE notificacion SET
fecha = #{ fecha , jdbcType = TIMESTAMP }

    , idTransaccion = #{ idTransaccion , jdbcType = VARCHAR }


    , resumen = #{ resumen , jdbcType = VARCHAR }


    , descripcion = #{ descripcion , jdbcType = VARCHAR }


    , idTipoNotificacion = #{ idTipoNotificacion , jdbcType = INTEGER }

WHERE idNotificacion = #{ idNotificacion , jdbcType = VARCHAR }
