mapper.select:
SELECT idNotificacionLeida , idNotificacion , fecha , idUsuario
FROM notificacionLeida
WHERE 1=1

    AND idNotificacionLeida = #{ idNotificacionLeida , jdbcType = VARCHAR }


    AND idNotificacion = #{ idNotificacion , jdbcType = VARCHAR }


    AND idUsuario = #{ idUsuario , jdbcType = INTEGER }


    AND fecha = #{ fecha , jdbcType = TIMESTAMP }

LIMIT 0, 1

SELECT idNotificacionLeida , idNotificacion , fecha , idUsuario
FROM notificacionLeida
WHERE 1=1

    AND idNotificacionLeida = #{ idNotificacionLeida , jdbcType = VARCHAR }


    AND idNotificacion = #{ idNotificacion , jdbcType = VARCHAR }


    AND idUsuario = #{ idUsuario , jdbcType = INTEGER }


    AND fecha = #{ fecha , jdbcType = TIMESTAMP }

mapper.insert:
INSERT INTO notificacionLeida (

    idNotificacionLeida


    , idNotificacion


    , idUsuario


    , fecha

) VALUES (

    #{ idNotificacionLeida , jdbcType = VARCHAR }


    , #{ idNotificacion , jdbcType = VARCHAR }


    , #{ idUsuario , jdbcType = INTEGER }


    , #{ fecha , jdbcType = TIMESTAMP }

)

mapper.update:
UPDATE notificacionLeida SET
fecha = #{ fecha , jdbcType = TIMESTAMP }

    , idNotificacion = #{ idNotificacion , jdbcType = VARCHAR }


    , idUsuario = #{ idUsuario , jdbcType = INTEGER }

WHERE idNotificacionLeida = #{ idNotificacionLeida , jdbcType = VARCHAR }
