SELECT idPaciente, nombreCompleto, apellidoPaterno, apellidoMaterno, sexo, fechaNacimiento, rfc, curp,
       idTipoPaciente, idUnidadMedica, claveDerechohabiencia, idEstatusPaciente, pacienteParticular,
       pacienteNumero, idEstadoCivil, idEscolaridad, idGrupoEtnico, idGrupoSanguineo, idReligion,
       idNivelSocioeconomico, idTipoVivienda, idOcupacion, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario
       , lugarNacimiento , paisResidencia , presentaDiscapacidad , descripcionDiscapacidad
FROM paciente
WHERE 1=1

    AND idPaciente = #{ idPaciente , jdbcType = VARCHAR }


    AND nombreCompleto = #{ nombreCompleto, , jdbcType = VARCHAR }


    AND pacienteNumero = #{ pacienteNumero , jdbcType = VARCHAR }

SELECT idpaciente, nombreCompleto, apellidoPaterno, apellidoMaterno, sexo, fechaNacimiento, rfc, curp, idTipoPaciente, idUnidadMedica, claveDerechohabiencia, idEstatusPaciente, pacienteParticular, pacienteNumero, idEstadoCivil, idEscolaridad, idGrupoEtnico, idGrupoSanguineo, idReligion, idNivelSocioeconomico, idTipoVivienda, idOcupacion, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario , lugarNacimiento , paisResidencia , presentaDiscapacidad , descripcionDiscapacidad FROM paciente WHERE 1=1
AND idpaciente = #{ idPaciente , jdbcType = VARCHAR }
AND ( nombreCompleto LIKE '%${ nombreCompleto }%' OR apellidoPaterno LIKE '%${ nombreCompleto }%' OR apellidoMaterno LIKE '%${ nombreCompleto }%' OR claveDerechohabiencia LIKE '%${ nombreCompleto }%' )
AND idEstructura = #{ idEstructura , jdbcType = VARCHAR}

SELECT * FROM ( SELECT p.idpaciente, p.nombreCompleto, p.apellidoPaterno, p.apellidoMaterno, p.sexo, p.fechaNacimiento, p.rfc, p.curp, p.idTipoPaciente, p.idUnidadMedica, p.claveDerechohabiencia, p.idEstatusPaciente, p.pacienteParticular, p.pacienteNumero, p.idEstadoCivil, p.idEscolaridad, p.idGrupoEtnico, p.idGrupoSanguineo, p.idReligion, p.idNivelSocioeconomico, p.idTipoVivienda, p.idOcupacion, p.idEstructura, p.insertFecha, p.insertIdUsuario, p.updateFecha, e.nombre as nombreEstructura, e.claveEstructura, etr.nombre as nombreEstructuraPeriferico, p.idEstructuraPeriferico, p.updateIdUsuario, ep.estatusPaciente, TIMESTAMPDIFF(YEAR, fechaNacimiento, CURDATE()) AS edadPaciente, IFNULL(c.idCama, 'Sin asignar') AS idCama, IFNULL(c.nombreCama, 'Sin asignar') AS nombreCama, IFNULL(c.ubicacion, 'Sin asignar') AS ubicacion, v.idVisita FROM paciente p INNER JOIN estructura e ON e.idEstructura = p.idEstructura LEFT JOIN estructura etr ON p.idEstructuraPeriferico = etr.idEstructura INNER JOIN estatusPaciente ep ON p.idEstatusPaciente = ep.idEstatusPaciente LEFT JOIN visita v ON (v.idPaciente = p.idPaciente AND v.fechaEgreso IS NULL) LEFT JOIN pacienteServicio ps ON (ps.idVisita = v.idVisita AND ps.fechaAsignacionFin IS NULL ) LEFT JOIN pacienteUbicacion pu ON (pu.idPacienteServicio = ps.idPacienteServicio and pu.fechaUbicaFin IS NULL) LEFT JOIN cama c ON c.idCama = pu.idCama WHERE 1=1
AND p.idEstructura = #{ idEstructura , jdbcType = VARCHAR}
AND p.idEstructura IN
#{ item.idEstructura }
GROUP BY v.fechaIngreso ORDER BY v.fechaIngreso desc , ps.fechaAsignacionInicio desc, pu.fechaUbicaInicio desc LIMIT #{numRegistros , jdbcType = INTEGER} ) AS resul GROUP BY resul.idPaciente;

