AND ( ( p.idEstructura = #{ idEstructura , jdbcType = VARCHAR }
)
OR ( p.idEstructuraPeriferico = #{ idEstructuraPeriferico , jdbcType = VARCHAR }
)
)
AND ( p.nombreCompleto LIKE '%${ cadenaBusqueda }%' OR p.apellidoPaterno LIKE '%${ cadenaBusqueda }%' OR p.apellidoMaterno LIKE '%${ cadenaBusqueda }%' OR p.claveDerechohabiencia LIKE '%${ cadenaBusqueda }%' OR p.pacienteNumero LIKE '%${ cadenaBusqueda }%' OR g.nombreCatalogo LIKE '%${ cadenaBusqueda }%' OR e.nombre LIKE '%${ cadenaBusqueda }%' )
AND p.idEstatusPaciente IN
#{item}
ORDER BY p.insertFecha DESC LIMIT #{ numRegistros , jdbcType = INTEGER }

