mapper.select:
SELECT p.idpaciente,p.nombreCompleto, p.apellidoPaterno, p.apellidoMaterno, p.sexo, p.fechaNacimiento, p.rfc, p.curp, p.idTipoPaciente, p.idUnidadMedica, p.claveDerechohabiencia, p.idEstatusPaciente, p.pacienteParticular, p.pacienteNumero, p.idEstadoCivil, p.idEscolaridad, p.idGrupoEtnico, p.idGrupoSanguineo, p.idReligion, p.idNivelSocioeconomico, p.idTipoVivienda, p.idOcupacion, p.idEstructura, p.insertFecha, p.insertIdUsuario, p.updateFecha, p.updateIdUsuario, ep.idEstatusPaciente, ep.estatusPaciente, concat(p.nombreCompleto,' ',p.apellidoPaterno,' ', p.apellidoMaterno) as nombres, TIMESTAMPDIFF(YEAR, fechaNacimiento, CURDATE()) AS edadPaciente, es.nombre AS nombreEstructura, esp.nombre AS nombreEstructuraPeriferico FROM paciente p INNER JOIN estatusPaciente ep ON p.idEstatusPaciente = ep.idEstatusPaciente LEFT JOIN estructura es ON es.idEstructura = p.idEstructura LEFT JOIN estructura esp ON esp.idEstructura = p.idEstructuraPeriferico WHERE 1 = 1
AND ( p.idEstructura = #{ idEstructura , jdbcType = VARCHAR } )
having nombres like '%${criterioBusqueda}%' OR p.claveDerechohabiencia like '%${criterioBusqueda}%' OR p.pacienteNumero like '%${criterioBusqueda}%' ORDER BY p.insertFecha DESC LIMIT #{numRegistros , jdbcType = INTEGER};

