mapper.select:
SELECT pc.idPlantilla , pc.nombre , pc.clave , pc.contenido , pc.asunto , pc.activo
, pc.insertFecha , pc.insertIdUsuario , pc.updateFecha , pc.updateIdUsuario
FROM plantillaCorreo pc

WHERE 1=1

    AND pc.idPlantilla = #{ idPlantilla , jdbcType = INTEGER }


    AND pc.nombre  = #{ nombre , jdbcType = VARCHAR }


    AND pc.clave = #{ clave , jdbcType = VARCHAR }


    AND pc.activo = #{ activo , jdbcType = INTEGER }

LIMIT 1

SELECT pc.idPlantilla , pc.nombre , pc.clave , pc.contenido , pc.asunto , pc.activo
, pc.insertFecha , pc.insertIdUsuario , pc.updateFecha , pc.updateIdUsuario
FROM plantillaCorreo pc 
WHERE 1=1

    AND pc.idPlantilla = #{ idPlantilla , jdbcType = INTEGER }


    AND pc.nombre  = #{ nombre , jdbcType = VARCHAR }


    AND pc.clave = #{ clave , jdbcType = VARCHAR }


    AND pc.activo = #{ activo , jdbcType = INTEGER }

mapper.insert:
INSERT INTO plantillaCorreo (
idPlantilla
, nombre
