, contenido
, asunto
, activo
, insertFecha
, insertidUsuario

    , updateFecha


    updateIdUsuario

) VALUES (
#{ idPlantilla , jdbcType = INTEGER }
, #{ nombre , jdbcType = VARCHAR }
, #{ clave , jdbcType = VARCHAR }
, #{ contenido , jdbcType = VARCHAR }
, #{ asunto , jdbcType = VARCHAR }
, #{ activo , jdbcType = INTEGER }
, #{ insertFecha , jdbcType = TIMESTAMP }
, #{ insertidUsuario , jdbcType = VARCHAR }

    , #{ updateFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }

)

mapper.update:
UPDATE plantillaCorreo SET
updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
, updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR }

    , nombre = #{ nombre , jdbcType = VARCHAR }


    , clave = #{ clave , jdbcType = VARCHAR }


    , contenido = #{ contenido , jdbcType = VARCHAR }


    , asunto = #{ asunto , jdbcType = VARCHAR }


    , activo = #{ activo , jdbcType = INTEGER }

WHERE idPlantilla = #{ idPlantilla , jdbcType = INTEGER }
