SELECT idReabastoInsumo, idReabasto, idInsumo, observaciones, cantidadSolicitada, cantidadComprometida, cantidadSurtida, cantidadRecibida, cantidadIngresada, idEstatusReabasto, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario,transferencia,idUsuarioAutoriza
FROM reabastoInsumo
WHERE 1=1
    AND idReabastoInsumo = #{idReabastoInsumo,jdbcType=VARCHAR}
          AND idReabasto = #{idReabasto,jdbcType=VARCHAR}
            AND idInsumo = #{idInsumo,jdbcType=VARCHAR}
       AND observaciones = #{observaciones,jdbcType=VARCHAR}
  AND cantidadSolicitada = #{cantidadSolicitada,jdbcType=INTEGER}
AND cantidadComprometida = #{cantidadComprometida,jdbcType=INTEGER}
     AND cantidadSurtida = #{cantidadSurtida,jdbcType=INTEGER}
    AND cantidadRecibida = #{cantidadRecibida,jdbcType=INTEGER}
   AND cantidadIngresada = #{cantidadIngresada,jdbcType=INTEGER}
   AND idEstatusReabasto = #{idEstatusReabasto,jdbcType=INTEGER}
         AND insertFecha = #{insertFecha,jdbcType=TIMESTAMP}
     AND insertIdUsuario = #{insertIdUsuario,jdbcType=VARCHAR}
         AND updateFecha = #{updateFecha,jdbcType=TIMESTAMP}
     AND updateIdUsuario = #{updateIdUsuario,jdbcType=VARCHAR}
       AND transferencia=#{transferencia}
   AND idUsuarioAutoriza=#{idUsuarioAutoriza};

SELECT idReabastoInsumo, idReabasto, idInsumo, observaciones, cantidadSolicitada, cantidadComprometida, cantidadSurtida, cantidadRecibida, cantidadIngresada, idEstatusReabasto, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario,transferencia,idUsuarioAutoriza
FROM reabastoInsumo
WHERE 1=1
    AND idReabastoInsumo = #{idReabastoInsumo,jdbcType=VARCHAR}
          AND idReabasto = #{idReabasto,jdbcType=VARCHAR}
            AND idInsumo = #{idInsumo,jdbcType=VARCHAR}
       AND observaciones = #{observaciones,jdbcType=VARCHAR}
  AND cantidadSolicitada = #{cantidadSolicitada,jdbcType=INTEGER}
AND cantidadComprometida = #{cantidadComprometida,jdbcType=INTEGER}
     AND cantidadSurtida = #{cantidadSurtida,jdbcType=INTEGER}
    AND cantidadRecibida = #{cantidadRecibida,jdbcType=INTEGER}
   AND cantidadIngresada = #{cantidadIngresada,jdbcType=INTEGER}
   AND idEstatusReabasto = #{idEstatusReabasto,jdbcType=INTEGER}
         AND insertFecha = #{insertFecha,jdbcType=TIMESTAMP}
     AND insertIdUsuario = #{insertIdUsuario,jdbcType=VARCHAR}
         AND updateFecha = #{updateFecha,jdbcType=TIMESTAMP}
     AND updateIdUsuario = #{updateIdUsuario,jdbcType=VARCHAR}
       AND transferencia=#{transferencia}
   AND idUsuarioAutoriza=#{idUsuarioAutoriza};

SELECT idReabastoInsumo,idReabasto,ri.idInsumo,observaciones,cantidadSolicitada, ri.cantidadComprometida,cantidadSurtida,cantidadRecibida,cantidadIngresada, idEstatusReabasto,ri.insertFecha,ri.insertIdUsuario,ri.updateFecha, ri.updateIdUsuario,m.claveInstitucional,m.nombreCorto, pm.nombrePresentacion,pm.idPresentacion,m.factorTransformacion,m.activo, IFNULL((SELECT sum(i.cantidadActual - IFNULL(vw.cantidadComprometida,0)) as cantidadActual FROM inventario i LEFT JOIN vwAlmacenInsumoComprometido vw ON (vw.idInsumo = i.idInsumo AND vw.idEstructuraAlmacen = i.idEstructura) WHERE i.idInsumo = ri.idInsumo AND i.idInsumo = m.idMedicamento AND i.idEstructura = #{idEstructura , jdbcType = VARCHAR} GROUP BY i.idInsumo,i.idEstructura),0) AS cantidadActual FROM reabastoInsumo ri INNER JOIN medicamento m ON ri.idInsumo = m.idMedicamento INNER JOIN presentacionMedicamento pm ON m.idPresentacionEntrada = pm.idPresentacion WHERE ri.idReabasto = #{idReabasto, jdbcType = VARCHAR}
AND ri.idEstatusReabasto IN
#{item}

SELECT e.nombre as almacen,e2.nombre as nombreProveedor,ri.idReabastoInsumo , m.claveInstitucional , m.nombreCorto , pm.nombrePresentacion , m.factorTransformacion , ri.cantidadSolicitada , m.activo , ri.cantidadSurtida , ri.idInsumo, ri.idEstatusReabasto, m.idPresentacionSalida as idPresentacion , pr.nombreProveedor as proveedorCompra , pr.idProveedor FROM reabastoInsumo ri JOIN medicamento m ON ri.idInsumo = m.idMedicamento JOIN presentacionMedicamento pm ON m.idPresentacionEntrada = pm.idPresentacion JOIN reabasto r on r.idReabasto = ri.idReabasto JOIN estructura e on e.idEstructura = r.idEstructura JOIN estructura e2 on e2.idEstructura = e.idEstructuraPadre LEFT JOIN ordenCompra oc on r.idReabasto = oc.idReabasto LEFT JOIN proveedor pr on oc.idProveedor = pr.idProveedor WHERE ri.idReabasto = #{ idReabasto , jdbcType = VARCHAR }
AND ri.idEstatusReabasto IN
#{item}

SELECT ri.idReabastoInsumo , m.claveInstitucional , m.nombreCorto , pm.nombrePresentacion , m.factorTransformacion , ri.cantidadSolicitada , m.activo , ri.cantidadSurtida , ri.idInsumo FROM reabastoInsumo ri INNER JOIN medicamento m ON ri.idInsumo = m.idMedicamento INNER JOIN presentacionMedicamento pm ON m.idPresentacionEntrada = pm.idPresentacion WHERE ri.idReabasto = #{ idReabasto , jdbcType = VARCHAR }
AND ri.idEstatusReabasto IN
#{item}
AND ( m.codigoBarrasAlterno = #{ cadena , jdbcType = VARCHAR }
OR m.nombreCorto LIKE '%${ cadena }%' OR m.nombreLargo LIKE '%${ cadena }%' OR m.claveAlterna LIKE '%${ cadena }%' OR m.claveInstitucional LIKE '%${ cadena }%'
)

SELECT factorTransformacion 
FROM medicamento
WHERE idMedicamento = #{idMedicamento, jdbcType = VARCHAR};

insert.foreach:

INSERT INTO reabastoInsumo
    (
        idReabastoInsumo ,
        idReabasto ,
        idInsumo ,
         observaciones,                 
        cantidadSolicitada ,
        cantidadComprometida ,
        cantidadSurtida ,
        cantidadRecibida ,
        cantidadIngresada ,
        idEstatusReabasto ,
        insertFecha ,
        insertIdUsuario 
        ,transferencia
        , idFolioAlternativo
        , idUsuarioAutoriza
    )
    SELECT            
        #{ item.idReabastoInsumo }
        , #{ item.idReabasto }
        , #{ item.idInsumo }
         , #{ item.observaciones }                
        , #{ item.cantidadSolicitada }
        , #{ item.cantidadComprometida }
        , #{ item.cantidadSurtida }
        , #{ item.cantidadRecibida }
        , #{ item.cantidadIngresada }
        , #{ item.idEstatusReabasto }
        , #{ item.insertFecha }
        , #{ item.insertIdUsuario }
         ,#{item.transferencia}
        ,#{item.idFolioAlternativo}    
        ,#{item.idUsuarioAutoriza}        
FROM DUAL WHERE
    NOT EXISTS( SELECT 
            *
        FROM 
            reabastoInsumo
        WHERE 
            idReabastoInsumo = #{ item.idReabastoInsumo })

update.foreach:
