mapper.select:
SELECT distinct i.lote,i.fechaCaducidad,i.cantidadActual,i.idInventario,r.idReabasto,r.idEstructura,r.idEstructuraPadre,m.nombreCorto,m.claveInstitucional,r.folio,ri.idReabastoInsumo,m.idMedicamento as idInsumo
from reabasto r 
inner join  reabastoInsumo ri on ri.idReabasto = r.idReabasto
inner join medicamento m on ri.idInsumo = m.idMedicamento
inner join reabastoEnviado re on re.idReabastoInsumo = ri.idReabastoInsumo
inner join inventario i on re.idInventarioSurtido = i.idInventario
inner join estructura e on e.idEstructura = r.idEstructura
where r.folio = #{folio , jdbcType = VARCHAR} 
and m.claveInstitucional = #{claveInstitucional , jdbcType = VARCHAR }
and i.lote = #{lote , jdbcType = VARCHAR}        
and i.fechaCaducidad = #{fechaCaducidad , jdbcType = TIMESTAMP}
and e.claveEstructura = #{claveEstructura, jdbcType = VARCHAR}

SELECT distinct e.nombre,i.lote,i.fechaCaducidad,i.cantidadActual,i.idInventario,m.nombreCorto,m.claveInstitucional,m.idMedicamento as idInsumo
from  medicamento m         
inner join inventario i on i.idInsumo = m.idMedicamento
inner join estructura e on e.idEstructura = i.idEstructura
where 
m.claveInstitucional = #{claveInstitucional , jdbcType = VARCHAR }
and i.lote = #{lote , jdbcType = VARCHAR}        
and i.fechaCaducidad = #{fechaCaducidad , jdbcType = TIMESTAMP}
and e.claveEstructura = #{claveEstructura, jdbcType = VARCHAR}

SELECT distinct e.nombre,i.lote,i.fechaCaducidad,i.cantidadActual,i.idInventario,m.nombreCorto,m.claveInstitucional,m.idMedicamento as idInsumo
from  medicamento m         
inner join inventario i on i.idInsumo = m.idMedicamento
inner join estructura e on e.idEstructura = i.idEstructura
where 
m.claveInstitucional = #{claveInstitucional , jdbcType = VARCHAR }
and i.lote = #{lote , jdbcType = VARCHAR}        
and i.fechaCaducidad = #{fechaCaducidad , jdbcType = TIMESTAMP}
and e.claveEstructura = #{claveEstructura, jdbcType = VARCHAR}                
and i.claveProveedor is null;

SELECT idReabastoInsumo, idReabasto, idInsumo, observaciones, cantidadSolicitada, cantidadComprometida, cantidadSurtida, cantidadRecibida, cantidadIngresada, idEstatusReabasto, insertFecha, insertIdUsuario, updateFecha, updateIdUsuario,transferencia 
FROM reabastoInsumo
WHERE idReabasto = #{ idReabasto, jdbcType = VARCHAR }
AND idinsumo = #{ idInsumo, jdbcType = VARCHAR }

SELECT e.nombre AS almacenServicio, m.claveInstitucional AS clave, m.nombreLargo AS nombreComercial, m.factorTransformacion AS piezasCaja, m.grupo, apc.minimo, apc.reorden, apc.maximo, apc.dotacion, apc.solicitud , 0 AS surtido, IFNULL(cpr.cantidadSurtida, 0) AS consumoReabasto, IFNULL(cpp.cantidadEnviada, 0) AS consumoPrescripcion, (IFNULL((SELECT cantidadActual FROM detalleInsumoSiam WHERE DATE_FORMAT(fechaRecepcion, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(apc.idAlmacen, apc.idMedicamento),'%Y-%m-%d') AND idInsumo = apc.idMedicamento AND idEstructura = apc.idAlmacen AND cantidadSurtida > 0 ORDER BY fechaRecepcion DESC LIMIT 1), 0) * m.factorTransformacion) AS cantidadRecibida, CASE WHEN e.idTipoAreaEstructura != 2 THEN (IFNULL((SELECT cantidadActual FROM detalleInsumoSiam ds WHERE DATE_FORMAT(fechaRecepcion, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(apc.idAlmacen, apc.idMedicamento), '%Y-%m-%d') AND ds.idInsumo = apc.idMedicamento AND ds.idEstructura = apc.idAlmacen AND cantidadSurtida > 0 ORDER BY fechaRecepcion DESC LIMIT 1), 0) * m.factorTransformacion) - (IFNULL(cpp.cantidadEnviada, 0)) ELSE 0 END AS cantidadServicio, CASE WHEN e.idTipoAreaEstructura = 2 THEN IF(SUM(i.cantidadActual)>=0,SUM(i.cantidadActual),0) ELSE 0 END AS cantidadAlmacen, (IF(e.idTipoAreaEstructura != 2, (IFNULL((SELECT cantidadActual FROM detalleInsumoSiam WHERE DATE_FORMAT(fechaRecepcion, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(apc.idAlmacen, apc.idMedicamento),'%Y-%m-%d') AND idInsumo = apc.idMedicamento AND idEstructura = apc.idAlmacen AND cantidadSurtida > 0 ORDER BY fechaRecepcion DESC LIMIT 1),0) * m.factorTransformacion) - IFNULL(cpp.cantidadEnviada, 0), IF(e.idTipoAreaEstructura = 2,IFNULL(SUM(i.cantidadActual),0),0)) ) AS cantidadActual, IF(apc.maximo-(IF(e.idTipoAreaEstructura != 2, (IFNULL((SELECT cantidadActual FROM detalleInsumoSiam WHERE DATE_FORMAT(fechaRecepcion, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(apc.idAlmacen, apc.idMedicamento),'%Y-%m-%d') AND idInsumo = apc.idMedicamento AND idEstructura = apc.idAlmacen AND cantidadSurtida > 0 ORDER BY fechaRecepcion DESC LIMIT 1),0) * m.factorTransformacion) - IFNULL(cpp.cantidadEnviada, 0), IF(e.idTipoAreaEstructura = 2,IFNULL(SUM(i.cantidadActual),0),0)) )>=0, apc.maximo-(IF(e.idTipoAreaEstructura != 2, (IFNULL((SELECT cantidadActual FROM detalleInsumoSiam WHERE DATE_FORMAT(fechaRecepcion, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(apc.idAlmacen, apc.idMedicamento),'%Y-%m-%d') AND idInsumo = apc.idMedicamento AND idEstructura = apc.idAlmacen AND cantidadSurtida > 0 ORDER BY fechaRecepcion DESC LIMIT 1),0) * m.factorTransformacion) - IFNULL(cpp.cantidadEnviada, 0), IF(e.idTipoAreaEstructura = 2,IFNULL(SUM(i.cantidadActual),0),0)) ), 0) AS cantidadSolicitada, apc.idAlmacenPuntosControl, e.idEstructura, m.idMedicamento AS idInsumo FROM almacenPuntosControl apc JOIN estructura e ON apc.idAlmacen = e.idEstructura JOIN medicamento m ON apc.idMedicamento = m.idMedicamento LEFT JOIN inventario i ON (apc.idAlmacen = i.idEstructura AND apc.idMedicamento = i.idInsumo) LEFT JOIN (SELECT r.idEstructura, ri.idInsumo, IFNULL(SUM(ri.cantidadSurtida), 0) AS cantidadSurtida FROM reabasto r JOIN reabastoInsumo ri ON ri.idReabasto = r.idReabasto WHERE DATE_FORMAT(r.fechaSurtida, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(idEstructura, idInsumo), '%Y-%m-%d') GROUP BY r.idEstructura , ri.idInsumo ) AS cpr ON (apc.idAlmacen = cpr.idEstructura AND apc.idMedicamento = cpr.idInsumo) LEFT JOIN (SELECT p.idEstructura, pi.idInsumo, IFNULL(SUM(si.cantidadEnviada), 0) AS cantidadEnviada FROM prescripcion p INNER JOIN prescripcionInsumo pi ON pi.idPrescripcion = p.idPrescripcion INNER JOIN surtimiento s ON s.idPrescripcion = p.idPrescripcion INNER JOIN surtimientoInsumo si ON (si.idSurtimiento = s.idSurtimiento AND si.idPrescripcionInsumo = pi.idPrescripcionInsumo) WHERE p.tipoConsulta = 'I' AND si.idEstatusSurtimiento NOT IN (1 , 5, 9) AND DATE_FORMAT(si.fechaEnviada, '%Y-%m-%d') >= DATE_FORMAT(FUNBUSCAULTIMAFECHACOLECTIVASURTIDA(p.idEstructura, pi.idInsumo), '%Y-%m-%d') GROUP BY p.idEstructura , pi.idInsumo ) cpp ON (apc.idAlmacen = cpp.idEstructura AND apc.idMedicamento = cpp.idInsumo) WHERE apc.activo = 1 AND e.activa = 1 AND m.activo = 1 AND apc.solicitud = 0 AND apc.idAlmacen =#{ idEstructura , jdbcType = VARCHAR }
AND apc.idMedicamento = #{idInsumo , jdbcType = VARCHAR }
GROUP BY e.nombre , m.claveInstitucional , m.nombreCorto , m.factorTransformacion , apc.maximo

SELECT r.folio , r.fechaSolicitud , r.fechaRecepcion , r.idUsuarioSolicitud , r.idUsuarioRecepcion , ri.idReabastoInsumo , ri.idReabasto , ri.idInsumo , ri.observaciones , ri.cantidadSolicitada , ri.cantidadSurtida , ri.cantidadRecibida , IFNULL(CONCAT(u1.nombre, ' ', u1.apellidoPaterno, ' ', u1.apellidoMaterno), '') AS nombrePersonaSolicita , IFNULL(CONCAT(u2.nombre, ' ', u2.apellidoPaterno, ' ', u2.apellidoMaterno), '') AS nombrePersonaRecibe , IFNULL(er.nombre ,'') AS estatusReabasto FROM reabasto r JOIN reabastoInsumo ri ON r.idReabasto = ri.idReabasto LEFT JOIN usuarios u1 ON r.idUsuarioSolicitud = u1.idUsuario LEFT JOIN usuarios u2 ON r.idUsuarioRecepcion = u2.idUsuario LEFT JOIN estatusReabasto er ON r.idEstatusReabasto = er.idEstatusReabasto WHERE 1 = 1
AND r.idEstructura = #{ idEstructura , jdbcType = VARCHAR }
AND ri.idInsumo = #{ idInsumo , jdbcType = VARCHAR }
