SELECT distinct mi.idMovimientoInventario, IF(tmo.tipoMovimiento = 'S', mi.idEstrutcuraOrigen, mi.idEstrutcuraDestino) AS idAlmacen, IF(tmo.tipoMovimiento = 'S', es.nombre, e.nombre) AS nombreAlmacen, tmo.descripcion AS tipoMovimiento, me.claveInstitucional AS claveMedicamento, me.nombreCorto AS nombreMedicamento, i.lote AS lote, mi.cantidad AS cantidad, mi.folioDocumento AS folio, mi.idUsuarioMovimiento AS idUsuario, u.nombreUsuario AS nombreUsuario, mi.fecha AS fechaMovimiento, i.cantidadXCaja AS cantidadXCaja, IF(mi.fechaCaducidad is null,i.fechaCaducidad,mi.fechaCaducidad) AS fechaCaducidad, i.idTipoOrigen
,p.idPrescripcion ,concat(uu.nombre , " " , uu.apellidoPaterno, " ", uu.apellidoMaterno) AS medico ,concat(pp.nombreCompleto, " " , pp.apellidoPaterno, " " , pp.apellidoMaterno ) AS paciente ,p.folio as folioReceta ,tm.descripcion as justificacion
FROM movimientoInventario mi INNER JOIN tipoMotivo tm ON mi.idTipoMotivo = tm.idTipoMotivo INNER JOIN tipoMovimiento tmo ON tm.idTipoMovimiento = tmo.idTipoMovimiento INNER JOIN inventario i ON mi.idInventario = i.idInventario INNER JOIN medicamento me ON i.idInsumo = me.idMedicamento LEFT JOIN estructura e ON mi.idEstrutcuraDestino = e.idEstructura LEFT JOIN estructura es ON mi.idEstrutcuraOrigen = es.idEstructura INNER JOIN usuarios u ON mi.idUsuarioMovimiento = u.idUsuario
LEFT JOIN surtimiento s on mi.folioDocumento = s.folio LEFT JOIN prescripcion p ON p.idPrescripcion = s.idPrescripcion
LEFT JOIN usuarios uu ON uu.idUsuario = p.idMedico LEFT JOIN pacienteServicio ps ON ps.idPacienteServicio = p.idPacienteServicio LEFT JOIN visita v ON v.idVisita = ps.idVisita LEFT JOIN paciente pp ON pp.idPaciente = v.idPaciente
where 1 = 1
AND mi.fecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND tmo.idTipoMovimiento IN
#{item}
AND me.idMedicamento IN
#{item.idMedicamento}
AND mi.idUsuarioMovimiento IN
#{item.idUsuario}
AND IF(tmo.tipoMovimiento = 'S' , mi.idEstrutcuraOrigen, mi.idEstrutcuraDestino) = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
ORDER BY mi.fecha ASC LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

SELECT COUNT(*) AS total FROM (SELECT DISTINCT mi.idMovimientoInventario, tmo.tipoMovimiento, tmo.descripcion, me.claveInstitucional, me.nombreCorto, i.lote, mi.cantidad, mi.folioDocumento, mi.idUsuarioMovimiento, u.nombreUsuario, mi.fecha, i.cantidadXCaja, i.fechaCaducidad, i.idTipoOrigen
, p.folio
FROM movimientoInventario mi
INNER JOIN tipoMotivo tm ON mi.idTipoMotivo = tm.idTipoMotivo INNER JOIN tipoMovimiento tmo ON tm.idTipoMovimiento = tmo.idTipoMovimiento INNER JOIN inventario i ON mi.idInventario = i.idInventario INNER JOIN medicamento me ON i.idInsumo = me.idMedicamento LEFT JOIN estructura e ON mi.idEstrutcuraDestino = e.idEstructura LEFT JOIN estructura es ON mi.idEstrutcuraOrigen = es.idEstructura INNER JOIN usuarios u ON mi.idUsuarioMovimiento = u.idUsuario
LEFT JOIN surtimiento s on mi.folioDocumento = s.folio LEFT JOIN prescripcion p ON p.idPrescripcion = s.idPrescripcion
LEFT JOIN usuarios uu ON uu.idUsuario = p.idMedico LEFT JOIN pacienteServicio ps ON ps.idPacienteServicio = p.idPacienteServicio LEFT JOIN visita v ON v.idVisita = ps.idVisita LEFT JOIN paciente pp ON pp.idPaciente = v.idPaciente
WHERE 1 = 1
AND mi.fecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND tmo.idTipoMovimiento IN
#{item}
AND me.idMedicamento IN
#{item.idMedicamento}
AND mi.idUsuarioMovimiento IN
#{item.idUsuario}
AND IF(tmo.tipoMovimiento = 'S' , mi.idEstrutcuraOrigen, mi.idEstrutcuraDestino) = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
) as result

select p.folio , p.fechaFirma , s.folio as folioSurtimiento , s.fechaProgramada , m.claveInstitucional as claveMedicamento, m.nombreCorto as nombreMedicamento , s.procesado , se.cantidadEnviado as insumoEnviado , si.cantidadSolicitada as insumoSolictado , si.cantidadEnviada as insumoCantEnviada, ifnull(si.cantidadVale,0) as cantidadVale, folioVale from prescripcion p inner join prescripcionInsumo pi on p.idPrescripcion = pi.idPrescripcion inner join surtimiento s on p.idPrescripcion = s.idPrescripcion inner join surtimientoInsumo si on s.idSurtimiento = si.idSurtimiento and pi.idPrescripcionInsumo = si.idPrescripcionInsumo inner join surtimientoEnviado se on si.idSurtimientoInsumo = se.idSurtimientoInsumo inner join medicamento m on pi.idInsumo = m.idMedicamento inner join inventario i on se.idInventarioSurtido = i.idInventario where si.cantidadVale > 0 and folioVale is not null
AND s.fechaProgramada between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND m.idMedicamento IN
#{item.idMedicamento}
AND se.insertIdUsuario IN
#{item.idUsuario}
AND s.idEstructuraAlmacen = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
order by s.fechaProgramada
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

SELECT COUNT(*) AS total FROM prescripcion p
inner join prescripcionInsumo pi on p.idPrescripcion = pi.idPrescripcion
inner join surtimiento s on p.idPrescripcion = s.idPrescripcion
inner join surtimientoInsumo si on s.idSurtimiento = si.idSurtimiento and pi.idPrescripcionInsumo = si.idPrescripcionInsumo
inner join surtimientoEnviado se on si.idSurtimientoInsumo = se.idSurtimientoInsumo
inner join medicamento m on pi.idInsumo = m.idMedicamento
inner join inventario i on se.idInventarioSurtido = i.idInventario where si.cantidadVale > 0  and folioVale is not null


    AND s.fechaProgramada between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }



    AND m.idMedicamento IN 

