SELECT p.folio, p.fechaFirma, concat(u.nombre, ' ', u.apellidoPaterno) as medico, CONCAT(pac.nombreCompleto, ' ', pac.apellidoPaterno, ' ', pac.apellidoMaterno) AS paciente, s.folio AS folioSurtimiento, s.fechaProgramada, m.claveInstitucional AS claveMedicamento, m.nombreCorto AS nombreMedicamento, i.lote, i.fechaCaducidad, s.procesado, se.cantidadEnviado AS insumoEnviado, si.cantidadSolicitada AS insumoSolictado, si.cantidadEnviada AS insumoCantEnviada, IFNULL(si.cantidadVale, 0) AS cantidadVale, IFNULL(si.folioVale, '') AS folioVale, i.cantidadXCaja, cg.nombreCatalogo AS cobertura, ep.estatus, tor.nombre AS origen, p.fechaPrescripcion, IF(si.idEstatusSurtimiento = 5, si.fechaCancela, si.fechaEnviada) AS fechaSurtCanc FROM prescripcion p INNER JOIN prescripcionInsumo pi ON p.idPrescripcion = pi.idPrescripcion INNER JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion INNER JOIN surtimientoInsumo si ON s.idSurtimiento = si.idSurtimiento AND pi.idPrescripcionInsumo = si.idPrescripcionInsumo INNER JOIN surtimientoEnviado se ON si.idSurtimientoInsumo = se.idSurtimientoInsumo INNER JOIN medicamento m ON pi.idInsumo = m.idMedicamento INNER JOIN inventario i ON se.idInventarioSurtido = i.idInventario LEFT JOIN usuarios u ON p.idMedico = u.idUsuario INNER JOIN pacienteServicio ps ON ps.idPacienteServicio = p.idPacienteServicio INNER JOIN visita v ON v.idVisita = ps.idVisita LEFT JOIN paciente pac ON pac.idPaciente = v.idPaciente LEFT JOIN estatusPrescripcion ep ON ep.idEstatusPrescripcion = p.idEstatusPrescripcion LEFT JOIN tipoOrigen tor ON tor.idTipoOrigen = i.idTipoOrigen LEFT JOIN catalogoGeneral cg ON cg.idCatalogoGeneral = pac.idTipoPaciente INNER JOIN estructura e on e.idEstructura = p.idEstructura WHERE 1=1
AND s.idEstatusSurtimiento >= 2 AND si.idEstatusSurtimiento >= 2 AND se.idEstatusSurtimiento >= 2
AND pi.idEstatusPrescripcion = 3
AND p.idEstatusPrescripcion = 3
AND p.tipoPrescripcion = #{paramBusquedaReporte.tipoPrescripcion, jdbcType = VARCHAR}
AND tor.idTipoOrigen = #{paramBusquedaReporte.idTipoOrigen, jdbcType = INTEGER}
AND ep.idEstatusPrescripcion = #{paramBusquedaReporte.idEstatusPrescripcion, jdbcType = INTEGER}
AND p.idEstatusPrescripcion = #{paramBusquedaReporte.idEstatusPrescripcion, jdbcType = INTEGER}
AND pac.idPaciente IN
#{item.idPaciente}
AND s.fechaProgramada between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND p.fechaPrescripcion BETWEEN #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND p.folio = #{paramBusquedaReporte.folio, jdbcType = VARCHAR}
AND m.idMedicamento IN
#{item.idMedicamento}
AND se.insertIdUsuario IN
#{item.idUsuario}
AND p.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
order by s.fechaProgramada
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

select count(*) as TOTAL FROM prescripcion p INNER JOIN prescripcionInsumo pi ON p.idPrescripcion = pi.idPrescripcion INNER JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion INNER JOIN surtimientoInsumo si ON s.idSurtimiento = si.idSurtimiento AND pi.idPrescripcionInsumo = si.idPrescripcionInsumo INNER JOIN surtimientoEnviado se ON si.idSurtimientoInsumo = se.idSurtimientoInsumo INNER JOIN medicamento m ON pi.idInsumo = m.idMedicamento INNER JOIN inventario i ON se.idInventarioSurtido = i.idInventario LEFT JOIN usuarios u ON p.idMedico = u.idUsuario INNER JOIN pacienteServicio ps ON ps.idPacienteServicio = p.idPacienteServicio INNER JOIN visita v ON v.idVisita = ps.idVisita LEFT JOIN paciente pac ON pac.idPaciente = v.idPaciente LEFT JOIN estatusPrescripcion ep ON ep.idEstatusPrescripcion = p.idEstatusPrescripcion LEFT JOIN tipoOrigen tor ON tor.idTipoOrigen = i.idTipoOrigen LEFT JOIN catalogoGeneral cg ON cg.idCatalogoGeneral = pac.idTipoPaciente INNER JOIN estructura e on e.idEstructura = p.idEstructura WHERE 1=1
AND s.idEstatusSurtimiento >= 2 AND si.idEstatusSurtimiento >= 2 AND se.idEstatusSurtimiento >= 2
AND pi.idEstatusPrescripcion = 3
AND p.idEstatusPrescripcion = 3
AND p.tipoPrescripcion = #{paramBusquedaReporte.tipoPrescripcion, jdbcType = VARCHAR}
AND tor.idTipoOrigen = #{paramBusquedaReporte.idTipoOrigen, jdbcType = INTEGER}
AND ep.idEstatusPrescripcion = #{paramBusquedaReporte.idEstatusPrescripcion, jdbcType = INTEGER}
AND p.idEstatusPrescripcion = #{paramBusquedaReporte.idEstatusPrescripcion, jdbcType = INTEGER}
