#{item.idPaciente}
AND s.fechaProgramada between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND p.fechaPrescripcion BETWEEN #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND p.folio = #{paramBusquedaReporte.folio, jdbcType = VARCHAR}
AND m.idMedicamento IN
#{item.idMedicamento}
AND se.insertIdUsuario IN
#{item.idUsuario}
AND p.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }

SELECT DISTINCT r.folio, r.fechaSolicitud AS fechaFirma, mi.folioDocumento AS folioSurtimiento, e.nombre AS paciente, i.lote, i.fechaCaducidad, m.claveInstitucional AS claveMedicamento, m.nombreCorto AS nombreMedicamento, u.nombreUsuario, ri.cantidadSurtida, re.cantidadEnviado AS insumoCantEnviada, ri.cantidadSolicitada AS insumoSolictado, re.cantidadEnviado AS insumoEnviado, i.cantidadXCaja, tor.nombre AS origen, mi.fecha AS fechaSurtCanc FROM reabasto r INNER JOIN reabastoInsumo ri ON r.idReabasto = ri.idReabasto INNER JOIN reabastoEnviado re ON ri.idReabastoInsumo = re.idReabastoInsumo INNER JOIN medicamento m ON ri.idInsumo = m.idMedicamento LEFT JOIN inventario i ON re.idInventarioSurtido = i.idInventario INNER JOIN movimientoInventario mi ON r.folio = mi.folioDocumento INNER JOIN estructura e ON r.idEstructura = e.idEstructura LEFT JOIN tipoOrigen tor ON i.idTipoOrigen = tor.idTipoOrigen INNER JOIN usuarios u ON r.idUsuarioSolicitud = u.idUsuario WHERE 1 = 1
AND r.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
AND tor.idTipoOrigen = #{paramBusquedaReporte.idTipoOrigen, jdbcType = INTEGER}
AND r.fechaSolicitud between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND m.idMedicamento IN
#{item.idMedicamento}
AND re.insertIdUsuario IN
#{item.idUsuario}
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

SELECT COUNT(*) AS total FROM (SELECT DISTINCT r.folio, r.fechaSolicitud AS fechaFirma, mi.folioDocumento AS folioSurtimiento, e.nombre AS paciente, i.lote, i.fechaCaducidad, m.claveInstitucional AS claveMedicamento, m.nombreCorto AS nombreMedicamento, u.nombreUsuario, ri.cantidadSurtida, re.cantidadEnviado AS insumoCantEnviada, ri.cantidadSolicitada AS insumoSolictado, re.cantidadEnviado AS insumoEnviado, i.cantidadXCaja, tor.nombre AS origen, mi.fecha AS fechaSurtCanc FROM reabasto r INNER JOIN reabastoInsumo ri ON r.idReabasto = ri.idReabasto INNER JOIN reabastoEnviado re ON ri.idReabastoInsumo = re.idReabastoInsumo INNER JOIN medicamento m ON ri.idInsumo = m.idMedicamento LEFT JOIN inventario i ON re.idInventarioSurtido = i.idInventario INNER JOIN movimientoInventario mi ON r.folio = mi.folioDocumento INNER JOIN estructura e ON r.idEstructura = e.idEstructura LEFT JOIN tipoOrigen tor ON i.idTipoOrigen = tor.idTipoOrigen INNER JOIN usuarios u ON r.idUsuarioSolicitud = u.idUsuario
WHERE 1 = 1
AND r.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
AND tor.idTipoOrigen = #{paramBusquedaReporte.idTipoOrigen, jdbcType = INTEGER}
AND r.fechaSolicitud between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND m.idMedicamento IN
#{item.idMedicamento}
AND re.insertIdUsuario IN
#{item.idUsuario}
) AS t

SELECT p.folio, p.fechaFirma, r.fechaEnvio, IFNULL(r.mensaje, '') AS mensaje, IFNULL(r.codigo, '') AS codigo FROM prescripcion p INNER JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion LEFT JOIN respuestaConsumo r ON p.folio = r.idrespuestaConsumo WHERE p.idEstatusPrescripcion = 4 AND s.idEstatusSurtimiento = 5
AND s.fechaProgramada between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND p.folio = #{paramBusquedaReporte.folio, jdbcType = VARCHAR}
AND s.idEstructuraAlmacen = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
order by s.fechaProgramada
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

