SELECT
ed.nombre AS estructuraOrigen , eo.nombre AS estructuraDestino , ei.nombre AS estructuraInventario, mi.folioDocumento as folio, tmv.descripcion AS descripcionMovimiento, tm.descripcion AS descripcionMotivo, m.nombreCorto as nombreMedicamento, m.claveInstitucional as claveMedicamento, i.fechaCaducidad, i.lote, mi.fecha as fechaSurtida, mi.idUsuarioMovimiento as idUsuarioSolicita, u.nombre, mi.cantidad as cantidad
FROM movimientoInventario mi INNER JOIN estructura eo ON eo.idEstructura = mi.idEstrutcuraOrigen INNER JOIN estructura ed ON ed.idEstructura = mi.idEstrutcuraDestino INNER JOIN inventario i ON i.idInventario = mi.idInventario INNER JOIN estructura ei ON ei.idEstructura = i.idEstructura INNER JOIN medicamento m ON m.idMedicamento = i.idInsumo INNER JOIN usuarios u ON u.idUsuario = mi.idUsuarioMovimiento INNER JOIN tipoMotivo tm ON tm.idTipoMotivo = mi.idTipoMotivo INNER JOIN tipoMovimiento tmv ON tmv.idTipoMovimiento = tm.idTipoMovimiento WHERE 1=1
AND mi.fecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND u.idUsuario IN
#{item.idUsuario}
AND m.idMedicamento IN
#{item.idMedicamento}
AND (ed.idEstructura = #{paramBusquedaReporte.idEstructura, jdbcType = VARCHAR} OR eo.idEstructura = #{paramBusquedaReporte.idEstructura, jdbcType = VARCHAR})
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

select distinct tmv.descripcion as descripcionMovimiento, tm.descripcion as descripcionMotivo, p.folio , me.claveInstitucional as claveMedicamento, me.nombreCorto as nombreMedicamento, i.lote, i.fechaCaducidad, si.cantidadSolicitada as insumoSolictado, si.insertFecha as fechaSolicitada , si.insertIdUsuario as idUsuarioSolicita, se.cantidadEnviado , e.nombre as estructura, se.insertFecha as fechaSurtida, se.insertIdUsuario as idUsuarioSurte, se.cantidadRecibido as cantidadRecibida, se.updateFecha as fechaRecibida, se.insertIdUsuario as idUsuarioRecibe, '' as cantidadIngresada , null as fechaIngresada , '' AS idUsuarioIngresa from surtimiento s inner join prescripcion p on s.idPrescripcion = p.idPrescripcion inner join prescripcionInsumo pi on p.idPrescripcion = pi.idPrescripcion inner join surtimientoInsumo si on (s.idSurtimiento = si.idSurtimiento and pi.idPrescripcionInsumo = si.idPrescripcionInsumo) inner join medicamento me on pi.idInsumo = me.idMedicamento inner join surtimientoEnviado se on si.idSurtimientoInsumo = se.idSurtimientoInsumo inner join inventario i on se.idInventarioSurtido = i.idInventario
inner join movimientoInventario mi on i.idInventario = mi.idInventario and mi.folioDocumento = s.folio inner join tipoMotivo tm on tm.idTipoMotivo = mi.idTipoMotivo inner join tipoMovimiento tmv on tmv.idTipoMovimiento = tm.idTipoMovimiento inner join estructura e on mi.idEstrutcuraOrigen = e.idEstructura
where 1=1
AND se.insertFecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND se.insertIdUsuario IN
#{item.idUsuario}
AND me.idMedicamento IN
#{item.idMedicamento}
AND i.idEstructura = #{paramBusquedaReporte.idEstructura, jdbcType = VARCHAR}
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}
union
select distinct tmv.descripcion as descripcionMovimiento, tm.descripcion as descripcionMotivo, r.folio , me.claveInstitucional as claveMedicamento, me.nombreCorto as nombreMedicamento, re.loteEnv as lote, re.fechaCad as fechaCaducidad, ri.cantidadSolicitada as insumoSolictado, ri.insertFecha as fechaSolicitada , ri.insertIdUsuario as idUsuarioSolicita , re.cantidadEnviado , e.nombre as estructura, re.insertFecha as fechaSurtida , re.insertIdUsuario as idUsuarioSurte , re.cantidadRecibida , re.updateFecha as fechaRecibida , re.insertIdUsuario as idUsuarioRecibe , re.cantidadIngresada , re.updateFecha as fechaIngresada , re.insertIdUsuario as idUsuarioIngresa from reabasto r inner join reabastoInsumo ri on r.idReabasto = ri.idReabasto inner join medicamento me on ri.idInsumo = me.idMedicamento inner join reabastoEnviado re on ri.idReabastoInsumo = re.idReabastoInsumo
inner join movimientoInventario mi on re.idInventarioSurtido = mi.idInventario and mi.folioDocumento = r.folio inner join tipoMotivo tm on tm.idTipoMotivo = mi.idTipoMotivo inner join tipoMovimiento tmv on tmv.idTipoMovimiento = tm.idTipoMovimiento inner join estructura e on mi.idEstrutcuraOrigen = e.idEstructura
where 1=1
AND re.insertFecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND re.insertIdUsuario IN
#{item.idUsuario}
AND me.idMedicamento IN
#{item.idMedicamento}
AND re.idEstructura = #{paramBusquedaReporte.idEstructura, jdbcType = VARCHAR}
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

select count(*) as total from ( select distinct tmv.descripcion as descripcionMovimiento, tm.descripcion as descripcionMotivo, p.folio , me.claveInstitucional as claveMedicamento, me.nombreCorto as nombreMedicamento, i.lote, i.fechaCaducidad, si.cantidadSolicitada as insumoSolictado, si.insertFecha as fechaSolicitada , si.insertIdUsuario as idUsuarioSolicita, se.cantidadEnviado , e.nombre as estructura, se.insertFecha as fechaSurtida, se.insertIdUsuario as idUsuarioSurte, se.cantidadRecibido as cantidadRecibida, se.updateFecha as fechaRecibida, se.insertIdUsuario as idUsuarioRecibe, '' as cantidadIngresada , null as fechaIngresada , '' AS idUsuarioIngresa from surtimiento s inner join prescripcion p on s.idPrescripcion = p.idPrescripcion inner join prescripcionInsumo pi on p.idPrescripcion = pi.idPrescripcion inner join surtimientoInsumo si on (s.idSurtimiento = si.idSurtimiento and pi.idPrescripcionInsumo = si.idPrescripcionInsumo) inner join medicamento me on pi.idInsumo = me.idMedicamento inner join surtimientoEnviado se on si.idSurtimientoInsumo = se.idSurtimientoInsumo inner join inventario i on se.idInventarioSurtido = i.idInventario
inner join movimientoInventario mi on i.idInventario = mi.idInventario and mi.folioDocumento = s.folio inner join tipoMotivo tm on tm.idTipoMotivo = mi.idTipoMotivo inner join tipoMovimiento tmv on tmv.idTipoMovimiento = tm.idTipoMovimiento inner join estructura e on mi.idEstrutcuraOrigen = e.idEstructura
where 1=1
AND se.insertFecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND se.insertIdUsuario IN
#{item.idUsuario}
AND me.idMedicamento IN
#{item.idMedicamento}
AND i.idEstructura = #{paramBusquedaReporte.idEstructura, jdbcType = VARCHAR}
union select distinct tmv.descripcion as descripcionMovimiento, tm.descripcion as descripcionMotivo, r.folio , me.claveInstitucional as claveMedicamento, me.nombreCorto as nombreMedicamento, re.loteEnv as lote, re.fechaCad as fechaCaducidad, ri.cantidadSolicitada as insumoSolictado, ri.insertFecha as fechaSolicitada , ri.insertIdUsuario as idUsuarioSolicita , re.cantidadEnviado , e.nombre as estructura, re.insertFecha as fechaSurtida , re.insertIdUsuario as idUsuarioSurte , re.cantidadRecibida , re.updateFecha as fechaRecibida , re.insertIdUsuario as idUsuarioRecibe , re.cantidadIngresada , re.updateFecha as fechaIngresada , re.insertIdUsuario as idUsuarioIngresa from reabasto r inner join reabastoInsumo ri on r.idReabasto = ri.idReabasto inner join medicamento me on ri.idInsumo = me.idMedicamento inner join reabastoEnviado re on ri.idReabastoInsumo = re.idReabastoInsumo
inner join movimientoInventario mi on re.idInventarioSurtido = mi.idInventario and mi.folioDocumento = r.folio inner join tipoMotivo tm on tm.idTipoMotivo = mi.idTipoMotivo inner join tipoMovimiento tmv on tmv.idTipoMovimiento = tm.idTipoMovimiento inner join estructura e on mi.idEstrutcuraOrigen = e.idEstructura
where 1=1
AND re.insertFecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND re.insertIdUsuario IN
#{item.idUsuario}
AND me.idMedicamento IN
#{item.idMedicamento}
AND re.idEstructura = #{paramBusquedaReporte.idEstructura, jdbcType = VARCHAR}
) s1

