AND p.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR }
and p.folio like '${paramBusquedaReporte.tipoReceta}%'
group by m.claveInstitucional , m.nombreCorto , i.lote
UNION ALL
