select p.folio, p.fechaPrescripcion , pa.pacienteNumero , concat(pa.nombreCompleto,' ', pa.apellidoPaterno, ' ', pa.apellidoMaterno) as nombrePaciente , u.cedProfesional , ifnull(u.cedEspecialidad, '') as cedEspecialidad , concat(u.nombre ,' ', u.apellidoPaterno, ' ', u.apellidoMaterno) as nombreMedico , m.claveInstitucional , m.nombreCorto , pi.fechaInicio as fechaInicioTratamiento , pi.dosis / m.concentracion as dosis, pm.nombrePresentacion , pi.dosis as concentracion, uc.nombreUnidadConcentracion , pi.frecuencia, pi.duracion , sm.fechaProgramado , sm.fechaMinistrado, sm.cantidad, i.lote , i.fechaCaducidad , ifnull(sm.comentarios,'') as comentarios , em.estatus , ifnull(concat(us.nombre ,' ', us.apellidoPaterno, ' ', us.apellidoMaterno), '') as nombreUsuarioMinistro , e.nombre,cantidadMinistrada from prescripcion p inner join pacienteServicio ps on p.idPacienteServicio = ps.idPacienteServicio left join pacienteUbicacion pu on ps.idPacienteServicio = pu.idPacienteServicio inner join visita v on ps.idVisita = v.idVisita inner join paciente pa on v.idPaciente = pa.idPaciente inner join prescripcionInsumo pi on p.idPrescripcion = pi.idPrescripcion inner join surtimiento s on p.idPrescripcion = s.idPrescripcion inner join surtimientoInsumo si on (pi.idPrescripcionInsumo = si.idPrescripcionInsumo and s.idSurtimiento = si.idSurtimiento) inner join surtimientoEnviado se on si.idSurtimientoInsumo = se.idSurtimientoInsumo inner join surtimientoMinistrado sm on se.idSurtimientoEnviado = sm.idSurtimientoEnviado inner join usuarios u on p.idMedico = u.idUsuario inner join medicamento m on pi.idInsumo = m.idMedicamento inner join inventario i on se.idInventarioSurtido = i.idInventario left join usuarios us on sm.idUsuario = us.idUsuario inner join estatusMinistracion em on sm.idEstatusMinistracion = em.idEstatusMinistracion left join unidadConcentracion uc on m.idUnidadConcentracion = uc.idUnidadConcentracion left join presentacionMedicamento pm on m.idPresentacionSalida = pm.idPresentacion inner join estructura e on p.idEstructura = e.idEstructura where 1 = 1
and sm.fechaMinistrado between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
and p.folio = #{ paramBusquedaReporte.folio , jdbcType = VARCHAR}
and sm.idEstatusMinistracion = #{ paramBusquedaReporte.tipoMinistrado , jdbcType = INTEGER}
AND m.idMedicamento IN
#{item.idMedicamento}
AND sm.idUsuario IN
#{item.idUsuario}
AND pa.idPaciente IN
#{item.idPaciente}
AND u.idUsuario IN
#{item.idUsuario}
AND p.idEstructura IN
#{ item }
Order by p.fechaPrescripcion ASC , sm.fechaProgramado ASC LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

