alida = pm.idPresentacion inner join estructura e on p.idEstructura = e.idEstructura where 1 = 1
and sm.fechaMinistrado between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
and p.folio = #{ paramBusquedaReporte.folio , jdbcType = VARCHAR}
and sm.idEstatusMinistracion = #{ paramBusquedaReporte.tipoMinistrado , jdbcType = INTEGER}
AND m.idMedicamento IN
#{item.idMedicamento}
AND sm.idUsuario IN
#{item.idUsuario}
AND pa.idPaciente IN
#{item.idPaciente}
AND u.idUsuario IN
#{item.idUsuario}
AND p.idEstructura IN
#{ item }
) as total;

select claveInstitucional,nombreCorto,maximo,minimo,disponible,estatus,consumoPromedio,pendientePorRecibir,idEstructura,insertFecha, consumoEsperado,idTipoAlmacen,idMedicamento from ( SELECT replace(m.claveInstitucional,'.',' ')as claveInstitucional, m.nombreCorto, ap.maximo, ap.minimo, e.idTipoAlmacen, i.cantidadActual AS disponible,e.idEstructura,i.insertFecha,m.idMedicamento, CASE WHEN i.cantidadActual <= 0 THEN "N" WHEN i.cantidadActual > 1 AND i.cantidadActual <= ap.reorden THEN "D" WHEN i.cantidadActual > ap.maximo THEN "O" END AS estatus, 0 AS consumoPromedio, 0 AS pendientePorRecibir, 0 AS consumoEsperado FROM medicamento m INNER JOIN presentacionMedicamento p ON m.idPresentacionEntrada = p.idPresentacion INNER JOIN inventario i ON m.idMedicamento = i.idInsumo INNER JOIN estructura e ON i.idEstructura = e.idEstructura INNER JOIN almacenPuntosControl ap ON (i.idInsumo = ap.idMedicamento AND i.idEstructura = ap.idAlmacen) LEFT JOIN proveedor pr ON i.idProveedor = pr.idProveedor LEFT JOIN tipoOrigen tor ON i.idTipoOrigen = tor.idTipoOrigen LEFT JOIN reabastoEnviado re ON i.idInventario = re.idInventarioSurtido ) as tabl WHERE 1 = 1
AND idMedicamento IN
#{item.idMedicamento}
and estatus = #{ paramBusquedaReporte.estatusCantidadInsumo , jdbcType = VARCHAR}
and insertFecha between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
and idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR}
and idTipoAlmacen in ( 3, 4)
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

SELECT r.fechaSolicitud , r.folio , m.claveInstitucional , m.nombreCorto , ri.cantidadSolicitada , ri.cantidadSurtida , inv.costoUnidosis , (ri.cantidadSolicitada * inv.costoUnidosis) as importe FROM reabasto r INNER JOIN reabastoInsumo ri on r.idReabasto = ri.idReabasto INNER JOIN medicamento m on ri.idInsumo = m.idMedicamento INNER JOIN reabastoEnviado re on ri.idReabastoInsumo = re.idReabastoInsumo INNER JOIN inventario inv on (re.idInsumo = inv.idInsumo and re.loteEnv = inv.lote and date_format(re.fechaCad, '%d%m%Y') = date_format(inv.fechaCaducidad, '%d%m%Y')) INNER JOIN estructura e on inv.idEstructura = e.idEstructura INNER JOIN entidadHospitalaria en on en.idEntidadHospitalaria = e.idEntidadHospitalaria WHERE 1 = 1
AND r.fechaSolicitud between #{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP } AND #{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP }
AND m.tipo = #{paramBusquedaReporte.tipoInsumo, jdbcType = INTEGER } LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

