SELECT s1.idMovimientoInventario , s1.estructura AS nombreAlmacen, s1.claveInstitucional, s1.nombreCorto, s1.fecha, s1.nombreUsuario , s1.existenciaAnterior , s1.entradaPorSurtimiento, s1.salidaPorReceta , CASE WHEN (s1.existenciaAnterior + s1.entradaPorSurtimiento - s1.salidaPorReceta) >= 0 THEN s1.existenciaAnterior + s1.entradaPorSurtimiento - s1.salidaPorReceta ELSE 0 END AS existenciaActual , s1.descripcion AS motivo FROM ( SELECT mi.idMovimientoInventario , CASE WHEN tmo.tipoMovimiento = 'E' THEN ed.nombre ELSE eo.nombre END AS estructura , m.claveInstitucional , m.nombreCorto, mi.fecha, concat(u.nombre,' ', u.apellidoPaterno,' ',u.apellidoMaterno) as nombreUsuario , CASE WHEN e.idTipoAreaEstructura = 2 THEN mi.saldoAlmacen WHEN e.idTipoAreaEstructura = 3 THEN mi.saldoServicio END as existenciaAnterior , CASE WHEN tmo.tipoMovimiento = 'E' THEN mi.cantidad ELSE 0 END AS entradaPorSurtimiento , CASE WHEN (tmo.tipoMovimiento = 'S' OR tmo.tipoMovimiento = 'T') THEN mi.cantidad ELSE 0 END AS salidaPorReceta , tm.descripcion FROM movimientoInventario mi JOIN inventario i ON mi.idInventario = i.idInventario JOIN medicamento m on i.idInsumo = m.idMedicamento JOIN estructura e on i.idEstructura = e.idEstructura JOIN estructura eo on mi.idEstrutcuraOrigen = eo.idEstructura JOIN estructura ed on mi.idEstrutcuraDestino = ed.idEstructura JOIN usuarios u on mi.idUsuarioMovimiento = u.idUsuario JOIN tipoMotivo tm on mi.idTipoMotivo = tm.idTipoMotivo JOIN tipoMovimiento tmo on tm.idTipoMovimiento = tmo.idTipoMovimiento WHERE 1=1 AND mi.fecha BETWEEN #{paramLibMedControlados.fechaInicio, jdbcType = TIMESTAMP } AND #{paramLibMedControlados.fechaFin, jdbcType = TIMESTAMP }
AND CASE WHEN tmo.tipoMovimiento = 'E' THEN mi.idEstrutcuraDestino = #{ paramLibMedControlados.idEstructura , jdbcType = VARCHAR} ELSE mi.idEstrutCuraOrigen = #{ paramLibMedControlados.idEstructura , jdbcType = VARCHAR} END
AND m.idMedicamento IN
#{item.idMedicamento}
AND m.refrigeracion = 1
AND SUBSTRING( REPLACE(m.claveInstitucional, '.' , '') , 7) like '5%'
AND SUBSTRING( REPLACE(m.claveInstitucional, '.' , '') , 7) like '4%'
AND m.medicamentoControlado = 1
) as s1

SELECT count(*) FROM ( SELECT mi.idMovimientoInventario , CASE WHEN tmo.tipoMovimiento = 'E' THEN ed.nombre ELSE eo.nombre END AS estructura , m.claveInstitucional , m.nombreCorto, mi.fecha, concat(u.nombre,' ', u.apellidoPaterno,' ',u.apellidoMaterno) as nombreUsuario , CASE WHEN e.idTipoAreaEstructura = 2 THEN mi.saldoAlmacen WHEN e.idTipoAreaEstructura = 3 THEN mi.saldoServicio END as existenciaAnterior , CASE WHEN tmo.tipoMovimiento = 'E' THEN mi.cantidad ELSE 0 END AS entradaPorSurtimiento , CASE WHEN (tmo.tipoMovimiento = 'S' OR tmo.tipoMovimiento = 'T') THEN mi.cantidad ELSE 0 END AS salidaPorReceta , tm.descripcion FROM movimientoInventario mi JOIN inventario i ON mi.idInventario = i.idInventario JOIN medicamento m on i.idInsumo = m.idMedicamento JOIN estructura e on i.idEstructura = e.idEstructura JOIN estructura eo on mi.idEstrutcuraOrigen = eo.idEstructura JOIN estructura ed on mi.idEstrutcuraDestino = ed.idEstructura JOIN usuarios u on mi.idUsuarioMovimiento = u.idUsuario JOIN tipoMotivo tm on mi.idTipoMotivo = tm.idTipoMotivo JOIN tipoMovimiento tmo on tm.idTipoMovimiento = tmo.idTipoMovimiento WHERE 1=1 AND mi.fecha BETWEEN #{paramLibMedControlados.fechaInicio, jdbcType = TIMESTAMP } AND #{paramLibMedControlados.fechaFin, jdbcType = TIMESTAMP }
AND CASE WHEN tmo.tipoMovimiento = 'E' THEN mi.idEstrutcuraDestino = #{ paramLibMedControlados.idEstructura , jdbcType = VARCHAR} ELSE mi.idEstrutCuraOrigen = #{ paramLibMedControlados.idEstructura , jdbcType = VARCHAR} END
AND m.idMedicamento IN
#{item.idMedicamento}
AND m.refrigeracion = 1
AND SUBSTRING( REPLACE(m.claveInstitucional, '.' , '') , 7) like '5%'
AND SUBSTRING( REPLACE(m.claveInstitucional, '.' , '') , 7) like '4%'
AND m.medicamentoControlado = 1
) as s1

SELECT nombreAlmacen, DATE_FORMAT(fechaEnviada, '%Y-%m-%d') AS fechaEnviada, total,idEstructura, completa, (truncate((completa * 100) / total,2)) AS completaPor, parcial, (truncate((parcial * 100) / total,2)) AS parcialPor, enCeros, (truncate((enCeros * 100) / total,2)) AS enCerosPor, enNulo, (truncate((enNulo * 100) / total,2)) AS enNuloPor FROM (SELECT nombreAlmacen, fechaEnviada, (completa + parcial + enCeros + enNulo) AS total, completa, parcial, enCeros, enNulo, idEstructura FROM (SELECT nombreAlmacen, fechaEnviada, SUM(completa) AS completa, SUM(parcial) AS parcial, SUM(enCeros) AS enCeros, SUM(enNulo) AS enNulo, idEstructura FROM viewSurtimientoPrescripcionConsolidado group by nombreAlmacen, fechaEnviada ) AS s1) AS s2 where 1 = 1
and s2.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR}
AND s2.fechaEnviada between DATE_FORMAT(#{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP },'%Y-%m-%d') AND DATE_FORMAT(#{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP },'%Y-%m-%d')
LIMIT #{startingAt, jdbcType = INTEGER}, #{maxPerPage, jdbcType = INTEGER}

select count(*) as total from ( SELECT nombreAlmacen, DATE_FORMAT(fechaEnviada, '%Y-%m-%d') AS fechaEnviada, total,idEstructura, completa, (truncate((completa * 100) / total,2)) AS completaPor, parcial, (truncate((parcial * 100) / total,2)) AS parcialPor, enCeros, (truncate((enCeros * 100) / total,2)) AS enCerosPor, enNulo, (truncate((enNulo * 100) / total,2)) AS enNuloPor FROM (SELECT nombreAlmacen, fechaEnviada, (completa + parcial + enCeros + enNulo) AS total, completa, parcial, enCeros, enNulo, idEstructura FROM (SELECT nombreAlmacen, fechaEnviada, SUM(completa) AS completa, SUM(parcial) AS parcial, SUM(enCeros) AS enCeros, SUM(enNulo) AS enNulo, idEstructura FROM viewSurtimientoPrescripcionConsolidado group by nombreAlmacen, fechaEnviada ) AS s1) AS s2 where 1 = 1
and s2.idEstructura = #{ paramBusquedaReporte.idEstructura , jdbcType = VARCHAR}
AND s2.fechaEnviada between DATE_FORMAT(#{paramBusquedaReporte.fechaInicio, jdbcType = TIMESTAMP },'%Y-%m-%d') AND DATE_FORMAT(#{paramBusquedaReporte.fechaFin, jdbcType = TIMESTAMP },'%Y-%m-%d')
) as val

SELECT COUNT(*) AS total FROM prescripcion p JOIN estructura e ON p.idEstructura = e.idEstructura JOIN usuarios u ON p.idMedico = u.idUsuario JOIN estatusPrescripcion ep ON p.idEstatusPrescripcion = ep.idEstatusPrescripcion LEFT JOIN pacienteServicio ps ON p.idPacienteServicio = ps.idPacienteServicio LEFT JOIN visita v ON ps.idVisita = v.idVisita LEFT JOIN paciente pa ON v.idPaciente = pa.idPaciente JOIN prescripcionInsumo pin ON p.idPrescripcion = pin.idPrescripcion JOIN medicamento m ON pin.idInsumo = m.idMedicamento JOIN unidadConcentracion uc ON m.idUnidadConcentracion = uc.idUnidadConcentracion JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion JOIN estructura e2 ON s.idEstructuraAlmacen = e2.idEstructura JOIN estatusSurtimiento es ON s.idEstatusSurtimiento = es.idEstatusSurtimiento JOIN surtimientoInsumo si ON ( s.idSurtimiento = si.idSurtimiento AND pin.idPrescripcionInsumo = si.idPrescripcionInsumo ) LEFT JOIN surtimientoEnviado se ON si.idSurtimientoInsumo = se.idSurtimientoInsumo LEFT JOIN inventario i ON se.idInventarioSurtido = i.idInventario LEFT JOIN fabricante f ON i.idFabricante = f.idFabricante LEFT JOIN usuarios u2 ON si.idUsuarioEnviada = u2.idUsuario LEFT JOIN solucion s2 ON s.idSurtimiento = s2.idSurtimiento LEFT JOIN usuarios u3 ON s2.idUsuarioValPrescr = u3.idUsuario LEFT JOIN usuarios u4 ON s2.idUsuarioValida = u4.idUsuario LEFT JOIN usuarios u5 ON s2.idUsuarioPrepara = u5.idUsuario LEFT JOIN usuarios u6 ON s2.idUsuarioInspeccion = u6.idUsuario LEFT JOIN usuarios u7 ON s2.idUsuarioAcondiciona = u7.idUsuario LEFT JOIN usuarios u8 ON s2.idUsuarioEntrega = u8.idUsuario LEFT JOIN usuarios u9 ON s2.idUsuarioRecibe = u9.idUsuario WHERE 1=1
AND s.fechaProgramada BETWEEN #{ paramBusquedaReporte.fechaInicio , jdbcType = TIMESTAMP } AND #{ paramBusquedaReporte.fechaFin , jdbcType = TIMESTAMP }
AND m.tipo = #{ paramBusquedaReporte.tipoInsumo , jdbcType = INTEGER }
AND m.idMedicamento IN
#{ item , jdbcType = VARCHAR }
AND p.idMedico IN
#{ item , jdbcType = VARCHAR }
AND pa.idPaciente IN
#{ item , jdbcType = VARCHAR }
AND si.idUsuarioEnviada IN
#{ item , jdbcType = VARCHAR }
AND p.idEstructura IN
#{ item , jdbcType = VARCHAR }
AND s.idEstructuraAlmacen IN
#{ item , jdbcType = VARCHAR }
;

SELECT se.idSurtimientoEnviado , i.idInventario , p.idPrescripcion , s.idSurtimiento , si.idSurtimientoInsumo , s2.idSolucion , p.fechaPrescripcion , p.folio AS folioPrescripcion , ep.estatus AS estatusPrescripcion , e.nombre AS nombreServicio , CONCAT(u.nombre,' ',u.apellidoPaterno,' ',u.apellidoMaterno) nombreMedico , IFNULL(CONCAT(pa.nombreCompleto,' ',pa.apellidoPaterno,' ',pa.apellidoMaterno),'') AS nombrePaciente , m.claveInstitucional , m.nombreCorto , m.concentracion
, pin.dosis , pin.frecuencia , pin.duracion , uc.nombreUnidadConcentracion , s.folio AS folioSurtimiento , s.fechaProgramada , e2.nombre AS nombreAlmacen , es.estatus AS estatusSurtimiento , IFNULL(se.cantidadEnviado,0) AS cantidadEnviado , IFNULL(se.cantidadRecibido,0) AS cantidadRecibido , IFNULL(i.lote,'') AS lote , i.fechaCaducidad , IFNULL(f.nombreFabricante,'') AS nombreFabricante , s2.fechaValPrescr , IFNULL(s2.comentValPrescr,'') AS comentValPrescr , IFNULL(CONCAT(u3.nombreUsuario,' ',u3.apellidoPaterno,' ',u3.apellidoMaterno),'') AS usuarioValidaPresc , s2.fechaValida , IFNULL(s2.comentValOrdenPrep,'') AS comentValOrdenPrep , IFNULL(CONCAT(u4.nombreUsuario,' ',u4.apellidoPaterno,' ',u4.apellidoMaterno),'') AS usuarioValidaOP , si.fechaEnviada , IFNULL(CONCAT(u2.nombreUsuario,' ',u2.apellidoPaterno,' ',u2.apellidoMaterno),'') AS usuarioDispensa , s2.fechaPrepara , IFNULL(s2.comentariosPreparacion ,'') AS comentariosPreparacion , IFNULL(CONCAT(u5.nombreUsuario,' ',u5.apellidoPaterno,' ',u5.apellidoMaterno),'') AS usuarioPrepara , s2.fechaInspeccion , IFNULL(s2.comentarioInspeccion ,'') AS comentarioInspeccion , IFNULL(CONCAT(u6.nombreUsuario,' ',u6.apellidoPaterno,' ',u6.apellidoMaterno),'') AS usuarioInspecciona , s2.fechaAcondicionamiento , IFNULL(s2.comentarioAcondicionada ,'') AS comentarioAcondicionada , IFNULL(CONCAT(u7.nombreUsuario,' ',u7.apellidoPaterno,' ',u7.apellidoMaterno),'') AS usuarioAcondiciona , s2.fechaEntrega , IFNULL(s2.comentariosEntrega,'') AS comentariosEntrega , IFNULL(CONCAT(u8.nombreUsuario,' ',u8.apellidoPaterno,' ',u8.apellidoMaterno),'') AS usuarioEntrega , IFNULL(CONCAT(u9.nombreUsuario,' ',u9.apellidoPaterno,' ',u9.apellidoMaterno),'') AS usuarioRecibe FROM prescripcion p JOIN estructura e ON p.idEstructura = e.idEstructura JOIN usuarios u ON p.idMedico = u.idUsuario JOIN estatusPrescripcion ep ON p.idEstatusPrescripcion = ep.idEstatusPrescripcion LEFT JOIN pacienteServicio ps ON p.idPacienteServicio = ps.idPacienteServicio LEFT JOIN visita v ON ps.idVisita = v.idVisita LEFT JOIN paciente pa ON v.idPaciente = pa.idPaciente JOIN prescripcionInsumo pin ON p.idPrescripcion = pin.idPrescripcion JOIN medicamento m ON pin.idInsumo = m.idMedicamento JOIN unidadConcentracion uc ON m.idUnidadConcentracion = uc.idUnidadConcentracion JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion JOIN estructura e2 ON s.idEstructuraAlmacen = e2.idEstructura JOIN estatusSurtimiento es ON s.idEstatusSurtimiento = es.idEstatusSurtimiento JOIN surtimientoInsumo si ON ( s.idSurtimiento = si.idSurtimiento AND pin.idPrescripcionInsumo = si.idPrescripcionInsumo ) LEFT JOIN surtimientoEnviado se ON si.idSurtimientoInsumo = se.idSurtimientoInsumo LEFT JOIN inventario i ON se.idInventarioSurtido = i.idInventario LEFT JOIN fabricante f ON i.idFabricante = f.idFabricante LEFT JOIN usuarios u2 ON si.idUsuarioEnviada = u2.idUsuario LEFT JOIN solucion s2 ON s.idSurtimiento = s2.idSurtimiento LEFT JOIN usuarios u3 ON s2.idUsuarioValPrescr = u3.idUsuario LEFT JOIN usuarios u4 ON s2.idUsuarioValida = u4.idUsuario LEFT JOIN usuarios u5 ON s2.idUsuarioPrepara = u5.idUsuario LEFT JOIN usuarios u6 ON s2.idUsuarioInspeccion = u6.idUsuario LEFT JOIN usuarios u7 ON s2.idUsuarioAcondiciona = u7.idUsuario LEFT JOIN usuarios u8 ON s2.idUsuarioEntrega = u8.idUsuario LEFT JOIN usuarios u9 ON s2.idUsuarioRecibe = u9.idUsuario WHERE 1=1
AND s.fechaProgramada BETWEEN #{ paramBusquedaReporte.fechaInicio , jdbcType = TIMESTAMP } AND #{ paramBusquedaReporte.fechaFin , jdbcType = TIMESTAMP }
AND m.tipo = #{ paramBusquedaReporte.tipoInsumo , jdbcType = INTEGER }
AND m.idMedicamento IN
#{ item , jdbcType = VARCHAR }
AND p.idMedico IN
#{ item , jdbcType = VARCHAR }
AND pa.idPaciente IN
#{ item , jdbcType = VARCHAR }
AND si.idUsuarioEnviada IN
#{ item , jdbcType = VARCHAR }
AND p.idEstructura IN
#{ item , jdbcType = VARCHAR }
AND s.idEstructuraAlmacen IN
#{ item , jdbcType = VARCHAR }
ORDER BY p.fechaPrescripcion ASC, s.fechaProgramada ASC LIMIT #{ paramBusquedaReporte.startingAt , jdbcType = INTEGER } , #{ paramBusquedaReporte.maxPerPage , jdbcType = INTEGER }

SELECT COUNT(*) AS total FROM ( SELECT DISTINCT p.idPrescripcion , s.idSurtimiento , s2.idSolucion , p.fechaFirma , p.folio AS folioPrescripcion , ep.estatus AS estatusPrescripcion , e.nombre AS nombreServicio , IFNULL(CONCAT(pa.nombreCompleto,' ',pa.apellidoPaterno,' ',pa.apellidoMaterno),'') AS nombrePaciente , s.fechaProgramada , s.folio AS folioSurtimiento , s2.descripcion AS descripcionMezcla , e2.nombre AS nombreAlmacen , es.estatus AS estatusSurtimiento , IFNULL(s2.loteMezcla, '') AS loteMezcla , IFNULL(s2.caducidadMezcla, '') AS caducidadMezcla , p.fechaPrescripcion , CONCAT(u.nombre,' ',u.apellidoPaterno,' ',u.apellidoMaterno) nombreMedico , IFNULL(s2.fechaValPrescr,'') AS fechaValPrescr , IFNULL(s2.comentValPrescr,'') AS comentValPrescr , IFNULL(CONCAT(u3.nombreUsuario,' ',u3.apellidoPaterno,' ',u3.apellidoMaterno),'') AS usuarioValidaPresc , IFNULL(s2.fechaValida,'') AS fechaValida , IFNULL(s2.comentValOrdenPrep,'') AS comentValOrdenPrep , IFNULL(CONCAT(u4.nombreUsuario,' ',u4.apellidoPaterno,' ',u4.apellidoMaterno),'') AS usuarioValidaOP , IFNULL( (SELECT sins.fechaEnviada FROM surtimientoInsumo sins WHERE sins.idSurtimiento = s.idSurtimiento LIMIT 1 ), '' ) AS fechaDispensacion , IFNULL(CONCAT(u2.nombreUsuario,' ',u2.apellidoPaterno,' ',u2.apellidoMaterno),'') AS usuarioDispensa , IFNULL(s2.fechaPrepara,'') AS fechaPrepara , IFNULL(s2.comentariosPreparacion ,'') AS comentariosPreparacion , IFNULL(CONCAT(u5.nombreUsuario,' ',u5.apellidoPaterno,' ',u5.apellidoMaterno),'') AS usuarioPrepara , IFNULL(s2.fechaInspeccion,'') AS fechaInspeccion , IFNULL(s2.comentarioInspeccion ,'') AS comentarioInspeccion , IFNULL(CONCAT(u6.nombreUsuario,' ',u6.apellidoPaterno,' ',u6.apellidoMaterno),'') AS usuarioInspecciona , IFNULL(s2.fechaAcondicionamiento,'') AS fechaAcondicionamiento , IFNULL(s2.comentarioAcondicionada ,'') AS comentarioAcondicionada , IFNULL(CONCAT(u7.nombreUsuario,' ',u7.apellidoPaterno,' ',u7.apellidoMaterno),'') AS usuarioAcondiciona , IFNULL(s2.fech