SELECT DISTINCT p.idPrescripcion , s.idSurtimiento , s2.idSolucion , p.fechaFirma , p.folio AS folioPrescripcion , ep.estatus AS estatusPrescripcion , e.nombre AS nombreServicio , IFNULL(CONCAT(pa.nombreCompleto,' ',pa.apellidoPaterno,' ',pa.apellidoMaterno),'') AS nombrePaciente , s.fechaProgramada , s.folio AS folioSurtimiento , s2.descripcion AS descripcionMezcla , e2.nombre AS nombreAlmacen , es.estatus AS estatusSurtimiento , IFNULL(s2.loteMezcla, '') AS loteMezcla , s2.caducidadMezcla , p.fechaPrescripcion , CONCAT(u.nombre,' ',u.apellidoPaterno,' ',u.apellidoMaterno) nombreMedico , s2.fechaValPrescr , IFNULL(s2.comentValPrescr,'') AS comentValPrescr , IFNULL(CONCAT(u3.nombreUsuario,' ',u3.apellidoPaterno,' ',u3.apellidoMaterno),'') AS usuarioValidaPresc , fechaValida , IFNULL(s2.comentValOrdenPrep,'') AS comentValOrdenPrep , IFNULL(CONCAT(u4.nombreUsuario,' ',u4.apellidoPaterno,' ',u4.apellidoMaterno),'') AS usuarioValidaOP , (SELECT sins.fechaEnviada FROM surtimientoInsumo sins WHERE sins.idSurtimiento = s.idSurtimiento LIMIT 1 ) AS fechaDispensacion , IFNULL(CONCAT(u2.nombreUsuario,' ',u2.apellidoPaterno,' ',u2.apellidoMaterno),'') AS usuarioDispensa , s2.fechaPrepara , IFNULL(s2.comentariosPreparacion ,'') AS comentariosPreparacion , IFNULL(CONCAT(u5.nombreUsuario,' ',u5.apellidoPaterno,' ',u5.apellidoMaterno),'') AS usuarioPrepara , s2.fechaInspeccion , IFNULL(s2.comentarioInspeccion ,'') AS comentarioInspeccion , IFNULL(CONCAT(u6.nombreUsuario,' ',u6.apellidoPaterno,' ',u6.apellidoMaterno),'') AS usuarioInspecciona , s2.fechaAcondicionamiento , IFNULL(s2.comentarioAcondicionada ,'') AS comentarioAcondicionada , IFNULL(CONCAT(u7.nombreUsuario,' ',u7.apellidoPaterno,' ',u7.apellidoMaterno),'') AS usuarioAcondiciona , s2.fechaEntrega , IFNULL(s2.comentariosEntrega,'') AS comentariosEntrega , IFNULL(CONCAT(u8.nombreUsuario,' ',u8.apellidoPaterno,' ',u8.apellidoMaterno),'') AS usuarioEntrega , ndc.fechaRecibe , IFNULL(ndc.userRecibe, '') AS usuarioRecibe , IFNULL(es2.descripcion, '') AS estatusSolucion , IFNULL(ndc.fechaRecibe, IFNULL(s2.fechaEntrega, IFNULL(s2.fechaAcondicionamiento, IFNULL(s2.fechaInspeccion, IFNULL(s2.fechaPrepara, s2.fechaValida))))) AS ultimoMovimiento , s2.idEstatusSolucion FROM prescripcion p JOIN estructura e ON p.idEstructura = e.idEstructura JOIN usuarios u ON p.idMedico = u.idUsuario JOIN estatusPrescripcion ep ON p.idEstatusPrescripcion = ep.idEstatusPrescripcion LEFT JOIN pacienteServicio ps ON p.idPacienteServicio = ps.idPacienteServicio LEFT JOIN visita v ON ps.idVisita = v.idVisita LEFT JOIN paciente pa ON v.idPaciente = pa.idPaciente JOIN prescripcionInsumo pin ON p.idPrescripcion = pin.idPrescripcion JOIN medicamento m ON pin.idInsumo = m.idMedicamento JOIN unidadConcentracion uc ON m.idUnidadConcentracion = uc.idUnidadConcentracion JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion JOIN estructura e2 ON s.idEstructuraAlmacen = e2.idEstructura JOIN estatusSurtimiento es ON s.idEstatusSurtimiento = es.idEstatusSurtimiento JOIN surtimientoInsumo si ON ( s.idSurtimiento = si.idSurtimiento AND pin.idPrescripcionInsumo = si.idPrescripcionInsumo ) LEFT JOIN surtimientoEnviado se ON si.idSurtimientoInsumo = se.idSurtimientoInsumo LEFT JOIN inventario i ON se.idInventarioSurtido = i.idInventario LEFT JOIN fabricante f ON i.idFabricante = f.idFabricante LEFT JOIN usuarios u2 ON si.idUsuarioEnviada = u2.idUsuario LEFT JOIN solucion s2 ON s.idSurtimiento = s2.idSurtimiento LEFT JOIN estatusSolucion es2 ON s2.idEstatusSolucion = es2.idEstatusSolucion LEFT JOIN usuarios u3 ON s2.idUsuarioValPrescr = u3.idUsuario LEFT JOIN usuarios u4 ON s2.idUsuarioValida = u4.idUsuario LEFT JOIN usuarios u5 ON s2.idUsuarioPrepara = u5.idUsuario LEFT JOIN usuarios u6 ON s2.idUsuarioInspeccion = u6.idUsuario LEFT JOIN usuarios u7 ON s2.idUsuarioAcondiciona = u7.idUsuario LEFT JOIN usuarios u8 ON s2.idUsuarioEntrega = u8.idUsuario LEFT JOIN usuarios u9 ON s2.idUsuarioRecibe = u9.idUsuario LEFT JOIN notaDispenColectDetalle ndcd ON s.idSurtimiento = ndcd.idSurtimiento LEFT JOIN notaDispenColect ndc ON ndcd.idNotaDispenColect = ndc.idNotaDispenColect AND ndc.idEstatusDispenColect != 2 WHERE 1=1
