SET idEstatusSurtimiento=#{item.idEstatusSurtimiento}
,updateFecha = now()
,updateIdUsuario =#{item.updateIdUsuario}
 , cantidadEnviado = #{ cantidadEnviado , jdbcType = INTEGER } 
WHERE 
    idSurtimientoEnviado = #{ item.idSurtimientoEnviado , jdbcType = VARCHAR }

mapper.select:
SELECT se.idSurtimientoEnviado , se.idSurtimientoInsumo , se.idInventarioSurtido , se.cantidadEnviado , se.cantidadRecibido , se.idEstatusSurtimiento , se.presentacionComercial , se.insertFecha , se.insertIdUsuario , se.updateFecha , se.updateIdUsuario , i.lote , i.fechaCaducidad , IFNULL(CONCAT(p2.nombreCompleto, ' ' , p2.apellidoPaterno, ' ', p2.apellidoMaterno), '') AS nombrePaciente , IFNULL(CONCAT(u1.nombre , ' ' , u1.apellidoPaterno , ' ', u1.apellidoMaterno), '') AS nombreMédico , IFNULL(CONCAT(u2.nombre , ' ' , u2.apellidoPaterno , ' ', u2.apellidoMaterno), '') AS nombreUsuarioSurte , IFNULL(CONCAT(u3.nombre , ' ' , u3.apellidoPaterno , ' ', u3.apellidoMaterno), '') AS nombreUsuarioPrepara , p.folio AS folioPrescripcion , s.folio AS folioSurtimiento , mi.fecha AS fechaMovimiento FROM prescripcion p JOIN prescripcionInsumo pi2 on p.idPrescripcion = pi2.idPrescripcion JOIN surtimiento s ON p.idPrescripcion = s.idPrescripcion JOIN surtimientoInsumo si ON (pi2.idPrescripcionInsumo = si.idPrescripcionInsumo AND s.idSurtimiento = si.idSurtimiento) JOIN surtimientoEnviado se ON si.idSurtimientoInsumo = se.idSurtimientoInsumo JOIN inventario i ON se.idInventarioSurtido = i.idInventario LEFT JOIN movimientoInventario mi ON s.folio = mi.folioDocumento LEFT JOIN pacienteServicio ps ON p.idPacienteServicio = ps.idPacienteServicio LEFT JOIN visita v ON ps.idVisita = v.idVisita LEFT JOIN paciente p2 ON v.idPaciente = p2.idPaciente LEFT JOIN usuarios u1 ON p.idMedico = u1.idUsuario LEFT JOIN usuarios u2 ON se.insertFecha = u2.idUsuario LEFT JOIN solucion so ON s.idSurtimiento = so.idSurtimiento LEFT JOIN usuarios u3 ON so.idUsuarioPrepara = u3.idUsuario WHERE 1 = 1 AND mi.idTipoMotivo in (20, 27, 33, 34)
AND s.idEstructuraAlmacen = #{ idEstructura , jdbcType = VARCHAR }
AND i.idInsumo = #{ idInsumo , jdbcType = VARCHAR }
AND i.idInventario = #{ idInventario , jdbcType = VARCHAR }
