mapper.select:
SELECT idTipoAlmacen , nombreTipoAlmacen , estatus , insertIdUsuario , insertFecha ,  updateIdUsuario , updateFecha
FROM tipoAlmacen
WHERE 1=1

    AND idTipoAlmacen = #{ idTipoAlmacen , jdbcType = INTEGER }


    AND nombreTipoAlmacen = #{ nombreTipoAlmacen , jdbcType = VARCHAR }


    AND estatus = #{ estatus , jdbcType = INTEGER }


	AND insertIdUsuario = #{ insertIdUsuario , jdbcType = VARCHAR }


	AND insertFecha = #{ insertFecha , jdbcType = TIMESTAMP }


	AND updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR}


    AND updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }

mapper.insert:
INSERT INTO tipoAlmacen (

    , nombreTipoAlmacen


    , estatus


    , insertIdUsuario


    , insertFecha


