mapper.select:
SELECT idTipoArchivo ,	nombre , descripcion , activo , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario 
FROM tipoArchivo
WHERE 1=1

    AND idTipoArchivo = #{ idTipoArchivo , jdbcType = INTEGER }


    AND nombre = #{ nombre , jdbcType = VARCHAR }


    AND descripcion = #{ descripcion , jdbcType = VARCHAR }


    AND activo = #{ activo , jdbcType = INTEGER }

LIMIT 0, 1

SELECT idTipoArchivo ,	nombre , descripcion , activo , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario 
FROM tipoArchivo
WHERE 1=1

    AND idTipoArchivo = #{ idTipoArchivo , jdbcType = INTEGER }


    AND nombre = #{ nombre , jdbcType = VARCHAR }


    AND descripcion = #{ descripcion , jdbcType = VARCHAR }


    AND activo = #{ activo , jdbcType = INTEGER }

SELECT idTipoArchivo ,	nombre , descripcion , activo , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario 
FROM tipoArchivo
WHERE activo = 1

    AND idTipoArchivo = #{ idTipoArchivo , jdbcType = INTEGER }


    AND nombre = #{ nombre , jdbcType = VARCHAR }


    AND descripcion = #{ descripcion , jdbcType = VARCHAR }

