mapper.select:
SELECT idTipoCama , nombreTipoCama , estatus , insertIdUsuario , insertFecha , updateIdUsuario , updateFecha
FROM tipoCama
WHERE 1=1

    AND idTipoCama = #{ idTipoCama , jdbcType = INTEGER }


    AND nombreTipoCama = #{ nombreTipoCama , jdbcType = VARCHAR }


    AND estatus = #{ estatus , jdbcType = INTEGER }


    AND insertIdUsuario = #{ insertIdUsuario , jdbcType = VARCHAR }


    AND insertFecha = #{ insertFecha , jdbcType = TIMESTAMP }


    AND updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR }


	AND updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }

mapper.insert:
INSERT INTO tipoCama (

    idTipoCama


    , nombreTipoCama


    , estatus


    , insertIdUsuario


    , insertFecha


    updateIdUsuario


    , updateFecha

) VALUES (

    #{ idTipoCama , jdbcType = INTEGER }


    , #{ nombreTipoCama , jdbcType = VARCHAR }


    , #{ estatus , jdbcType = INTEGER }


    , #{ insertIdUsuario , jdbcType = VARCHAR }


    , #{ insertFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }


    , #{ updateFecha , jdbcType = TIMESTAMP }

)

mapper.update:
UPDATE tipoCama SET
updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
, updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR }

    , nombreTipoCama = #{ nombreTipoCama , jdbcType = VARCHAR }


    , estatus = #{ estatus , jdbcType = INTEGER }

WHERE idTipoCama = #{ idTipoCama , jdbcType = INTEGER }

mapper.select:
SELECT idTipoCama, nombreTipoCama, estatus, insertIdUsuario, insertFecha, updateIdUsuario, updateFecha 
FROM tipoCama
WHERE nombreTipoCama=#{nombre}
