mapper.select:
SELECT idTipoSolucion , clave , descripcion, activo , insertFecha, insertIdUsuario , updateIdUsuario , updateFecha
FROM tipoSolucion
WHERE 1=1

    AND idTipoSolucion = #{ idTipoSolucion , jdbcType = VARCHAR }


    AND clave = #{ clave , jdbcType = VARCHAR }


    AND descripcion = #{ descripcion , jdbcType = INTEGER }


    AND activo = #{ activo , jdbcType = INTEGER }


    AND insertIdUsuario = #{ insertIdUsuario , jdbcType = VARCHAR }


    AND insertFecha = #{ insertFecha , jdbcType = TIMESTAMP }


    AND updateIdUsuario = #{ updateIdUsuario , jdbcType = VARCHAR }


	AND updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
