mapper.select:
SELECT idRol , idTransaccion , idAccion , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
FROM transaccionAcciones
WHERE 1=1

    AND idRol = #{ idRol , jdbcType = VARCHAR }


    AND idTransaccion = #{ idTransaccion , jdbcType = VARCHAR }


    AND idAccion = #{ `idAccion` , jdbcType = VARCHAR }

mapper.insert:
INSERT INTO transaccionAcciones (

    idRol


    , idTransaccion


    , idAccion


    , insertFecha


    , updateIdUsuario


    , updateFecha


    updateIdUsuario

) VALUES (

    #{ idRol , jdbcType = VARCHAR }


    , #{ idTransaccion , jdbcType = VARCHAR }


    , #{ idAccion , jdbcType = VARCHAR }


    , #{ insertFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }


    , #{ updateFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }

)

mapper.delete:
DELETE FROM transaccionAcciones 
WHERE idRol = #{ idRol , jdbcType = VARCHAR }
and  idTransaccion = #{ idTransaccion , jdbcType = VARCHAR }
and  idAccion = #{ idAccion , jdbcType = VARCHAR }
