mapper.select:
SELECT idTurnoMedico,
       idTurno,
       idMedico
FROM turnoMedico
WHERE 1 = 1

    AND idTurnoMedico = #{ idTurnoMedico , jdbcType = VARCHAR }


    AND idTurno = #{ idTurno , jdbcType = INTEGER }


    AND idMedico = #{ idMedico , jdbcType = VARCHAR }

mapper.insert:
