mapper.select:
SELECT idUsuario , idRol , insertFecha , insertIdUsuario , updateFecha , updateIdUsuario
FROM usuariosRoles
WHERE 1=1

    AND idUsuario = #{ idUsuario , jdbcType = VARCHAR }


    AND idRol = #{ idRol , jdbcType = VARCHAR }

mapper.insert:
INSERT INTO usuariosRoles (

    idUsuario


    , idRol


    , insertFecha


    , insertIdUsuario


    , updateFecha


    updateIdUsuario

) VALUES (

    #{ idUsuario , jdbcType = VARCHAR }


    , #{ idRol , jdbcType = VARCHAR }


    , #{ insertFecha , jdbcType = TIMESTAMP }


    , #{ insertIdUsuario , jdbcType = VARCHAR }


    , #{ updateFecha , jdbcType = TIMESTAMP }


    , #{ updateIdUsuario , jdbcType = VARCHAR }

)

mapper.update:
UPDATE usuariosRoles SET         
    updateFecha = #{ updateFecha , jdbcType = TIMESTAMP }
    , updateIdUsuario =#{ updateIdUsuario , jdbcType = VARCHAR }        

    ,idRol= #{ idRol , jdbcType = VARCHAR }

WHERE idUsuario = #{ idUsuario , jdbcType = VARCHAR }

mapper.delete:
DELETE FROM usuariosRoles 
WHERE idUsuario = #{ idUsuario , jdbcType = VARCHAR }
and  idRol = #{ idRol , jdbcType = VARCHAR }
