mapper.update:
UPDATE expPacking SET
    tratado = #{ expPacking.tratado , jdbcType = VARCHAR }
    , fechaTratado = #{ expPacking.fechaTratado , jdbcType = TIMESTAMP }
WHERE idexped = #{ expPacking.idExped , jdbcType = VARCHAR }
  AND articulo = #{ expPacking.articulo , jdbcType = VARCHAR }
  AND linea = #{ expPacking.linea , jdbcType = VARCHAR }
