mapper.select:
SELECT id,
    articulo,
    tipoAlmacen,
    minimo,
    maximo,
    maximoExtraccion,
    multiploReposicion,
    algoritmoEntrada,
    algoritmoSalida,
    entradas,
    salidas,
    repEnt,
    repSal,
    articuloEstatico,
    ordenSalida,
    tratado,
    error,
    fechaTratado,
    version,
    orden,
    fechaCreacion,
    maximoCanalesUa,
    rotacion,
    agrupaPicksSalida,
    maximoReposicion,
    neMultiploReposicion,
    neMaximoReposicion,
    neMaximo,
    neMinimo,
    neMaximoExtraccion
FROM impMaximosAlmacen
WHERE 1=1

    AND articulo = #{ articulo , jdbcType = VARCHAR }


    AND tipoAlmacen = #{ tipoAlmacen , jdbcType = VARCHAR }


    AND minimo = #{ minimo , jdbcType = DOUBLE }


    AND maximo = #{ maximo , jdbcType = DOUBLE }


    AND maximoExtraccion = #{ maximoExtraccion , jdbcType = DOUBLE }


    AND multiploReposicion = #{ multiploReposicion , jdbcType = DOUBLE }


    AND algoritmoEntrada = #{ algoritmoEntrada , jdbcType = VARCHAR }


    AND algoritmoSalida = #{ algoritmoSalida , jdbcType = VARCHAR }


    AND entradas = #{ entradas , jdbcType = INTEGER }


    AND salidas = #{ salidas , jdbcType = INTEGER }


    AND repEnt = #{ repEnt , jdbcType = INTEGER }


    AND repSal = #{ repSal , jdbcType = INTEGER }


    AND articuloEstatico = #{ articuloEstatico , jdbcType = INTEGER }


    AND ordenSalida = #{ ordenSalida , jdbcType = INTEGER }


    AND tratado = #{ tratado , jdbcType = VARCHAR }


    AND error = #{ error , jdbcType = VARCHAR }


    AND fechaTratado = #{ fechaTratado , jdbcType = TIMESTAMP }


    AND version = #{ version , jdbcType = INTEGER }


    AND orden = #{ orden , jdbcType = INTEGER }


    AND fechaCreacion = #{ fechaCreacion , jdbcType = TIMESTAMP }


    AND maximoCanalesUa = #{ maximoCanalesUa , jdbcType = INTEGER }


    AND rotacion = #{ rotacion , jdbcType = VARCHAR }


    AND agrupaPicksSalida = #{ agrupaPicksSalida , jdbcType = INTEGER }


    AND maximoReposicion = #{ maximoReposicion , jdbcType = DOUBLE }


    AND neMultiploReposicion = #{ neMultiploReposicion , jdbcType = INTEGER }


    AND neMaximoReposicion = #{ neMaximoReposicion , jdbcType = INTEGER }


    AND neMaximo = #{ neMaximo , jdbcType = INTEGER }


    AND neMinimo = #{ neMinimo , jdbcType = INTEGER }


    AND neMaximoExtraccion = #{ neMaximoExtraccion , jdbcType = INTEGER }

LIMIT 0, 1

